-- ----------------------------
DROP TABLE IF EXISTS `clang_about`;
CREATE TABLE `clang_about` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `content` text COMMENT '内容描述',
  `type` varchar(10) DEFAULT NULL COMMENT '关于我们、联系我们、版权信息',
  `time` datetime DEFAULT NULL COMMENT '发布时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of clang_about
-- ----------------------------
INSERT INTO `clang_about` VALUES ('1', '介绍我们自己的一些情况', '<p>\r\n	&nbsp; &nbsp; 传统的课堂的教学已经不能满足我校学生学习C语言的需求，根据调查同学们的建议，我们决定创建C语言网络教学平台，只是为了满足本校的同学学习上的需求，也为广大C语言爱好者听过一个学习和交流的平台，促进C语言的发展……\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 本站由本校C语言方面的大牛担任指导老师，他们可以在一定程度上指导大家学习C语言，少走一些弯路子，当然，你也可以不采纳老师们的建议，毕竟每个人的学习方法都不同，同学们要根据自身的情况，适当的采纳一些对自己学习上有效的方法对策。\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 为了更好的促进同学们在C语言方面的发展，也为了激起学习的热情，本站提供了一个上传平台，可以把自己的问题、学习心得、自己设计的程序上传到本站，与同学们一起共享资源，分享个中乐趣。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;让我们一起学习吧……\r\n</p>', '关于我们', '2013-12-10 09:33:48');
INSERT INTO `clang_about` VALUES ('2', '联系方式、方便交流', '<p>\r\n	<span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;如果你对本站有什么好的建议或者不懂的地方，可以参照照下面的方式联系我们，我们一定会为你解答：</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;一、邮件联系：cwang@163.com&nbsp;；</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;\">&nbsp; &nbsp; 二、QQ联系：123456789；学习交流群一号群：356963666</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;二号群：569878952<br />\r\n</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;\">&nbsp; &nbsp; 三、电话/短信联系：13688888888；</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;\">&nbsp;&nbsp;&nbsp;&nbsp;再次感谢大家对我们的支持，谢谢……</span> \r\n</p>', '联系我们', '2013-12-13 10:20:30');
INSERT INTO `clang_about` VALUES ('3', '不能及时回应，请留言', '不能及时回应，请留言', '留言反馈', '2013-11-21 11:36:48');
INSERT INTO `clang_about` VALUES ('4', '版权信息', '<div>\r\n	<div>\r\n		地址:广西宜州市庆远镇龙江路42号&nbsp;\r\n	</div>\r\n	<div>\r\n		邮编:546300&nbsp;\r\n	</div>\r\n	<div>\r\n		版权所有 2013 河池学院&nbsp;\r\n	</div>\r\n	<div>\r\n		Copyright@2013 All Rights Reserved&nbsp;\r\n	</div>\r\n</div>\r\n<div>\r\n</div>', '版权信息', '2013-12-10 09:13:31');

