INSERT INTO `clang_local` VALUES ('4', 'Go语言基础', 'golang是一门新兴的语言，作为服务器端语言还是不错，游戏开发效果也挺好的', '1386661997.jpg', '20131128200101.avi', 'hong', '2013-12-10 15:53:17', 'http://www.360yun.com', '3');
INSERT INTO `clang_local` VALUES ('5', 'C语言程序设计视频教程(曾怡)01', '这个视频不错，我把它分享到了 腾讯好友', '1386662090.jpg', '20131128200101.avi', 'hong', '2013-12-10 15:55:07', 'http://www.yun.com/', '6');
INSERT INTO `clang_local` VALUES ('6', 'printf的用法详解', 'printf的用法详解，如果不小心就有很多的问题，比如说不能打印数组、字符串等现象', '20131128200111.jpg', '20131128200111.swf', 'hong', '2013-12-10 16:45:54', null, '3');
INSERT INTO `clang_local` VALUES ('7', '选择 if 的范围问题', '选择 if 的范围问题选择 if 的范围问题，很多人不理解if的条件语句，在这里我会详细的跟大家讲解if的基本思路，然后举例证明', '20131128200112.jpg', '20131128200112.swf', 'hong', '2013-12-10 16:46:22', null, '6');
INSERT INTO `clang_local` VALUES ('8', 'if 的常见问题分析', 'if 的常见问题分析，一般来说，我们都习惯性的又肯定和否定的判断，但是在程序中我们往往只需要一种就可了', '20131128200113.jpg', '20131128200113.swf', 'hong', '2013-12-10 16:46:43', null, '8');
INSERT INTO `clang_local` VALUES ('9', '多重 FOR 循环执行的顺序', '多重 FOR 循环执行的顺序，循环的时候就怕分不清坐标，行与列分不清，所以就不知道数组下标该怎么表示', '20131128200114.jpg', '20131128200114.swf', 'hong', '2013-12-10 16:47:04', null, '8');

