INSERT INTO `clang_notice` VALUES ('2', '第十六周课表', '<table style=\"text-align:center;width:100%;\" cellpadding=\"2\" cellspacing=\"0\" border=\"1\" bordercolor=\"#000000\" align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					星期\r\n				</p>\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				时间\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				内容\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				教师\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					星期二\r\n				</p>\r\n				<p style=\"text-align:center;\">\r\n					<br />\r\n				</p>\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				晚上八点<br />\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				指针\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				杨老师\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					星期四\r\n				</p>\r\n				<p style=\"text-align:center;\">\r\n					<br />\r\n				</p>\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				晚上七点半\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				文件操作\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				云老师\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n				<p style=\"text-align:center;\">\r\n					星期五\r\n				</p>\r\n				<p style=\"text-align:center;\">\r\n					<br />\r\n				</p>\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				晚上九点\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				交流答疑\r\n			</td>\r\n			<td style=\"text-align:center;\">\r\n				赵老师\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	欢迎广大同学及时参加\r\n</p>\r\n<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\"></span> \r\n</div>', '本周课表', 'hong', '2013-11-22 15:48:10');
INSERT INTO `clang_notice` VALUES ('3', '三个系的运动会', '三个系的运动会三个系的运动会三个系的运动会', '最新公告', 'hong', '2013-11-22 16:02:58');
INSERT INTO `clang_notice` VALUES ('5', '河池学院计信学院2014届毕业论文（设计）初次答辩工作计划', '<div align=\"left\">\r\n	&nbsp; &nbsp; 指导教师应尽快督促学生完成毕业论文（设计）正文的定稿，并按毕业论文（设计）格式要求，并对学生的定稿进行合格审查，所有学生的正文必须在12月12日前在毕业论文管理系统(网址： <a href=\"http://222.218.130.75/\">http://222.218.130.75</a>:81/jixinxi/或<a href=\"http://www.hcnu.edu.cn/jixinxi\">http://www.hcnu.edu.cn/jixinxi</a>)提交。每个参加答辩的毕业生必须提前打印好一份完整的毕业论文（包括开题报告和正文），在答辩时供评委传阅。\r\n</div>', '最新公告', 'hong', '2013-11-22 16:41:45');

-- ----------------------------
-- Table structure for `clang_online`
-- ----------------------------
DROP TABLE IF EXISTS `clang_online`;
CREATE TABLE `clang_online` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `content` text COMMENT '内容描述',
  `pic` varchar(255) DEFAULT NULL COMMENT '视频截图地址',
  `url` varchar(255) DEFAULT NULL COMMENT '链接的网站地址',
  `author` varchar(20) DEFAULT NULL COMMENT '发布者',
  `time` datetime DEFAULT NULL COMMENT '发表时间',
  `click` int(11) DEFAULT NULL COMMENT '点击量',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of clang_online
-- ----------------------------
INSERT INTO `clang_online` VALUES ('1', 'C语言入门视频教程01', '<p>\r\n	C语言是一种计算机程序设计语言，既具有高级语言的特点，又具有汇编语言的特点。今天我们同大家分享的这部C语言入门视频教程是一部非常棒的教程。欢迎感兴趣的朋友来观看学习!\r\n</p>\r\n<p>\r\n	　　C语言的特点：1. C是高级语言。它把高级语言的基本结构和语句与低级语言的实用性结合起来。C \r\n语言可以像汇编语言一样对位、字节和地址进行操作，而这三者是计算机最基本的工作单元。\r\n</p>', '1386662674.jpg', 'http://video.1kejian.com/video/?24355-0-0.html', 'hong', '2013-12-10 16:04:34', '13');
INSERT INTO `clang_online` VALUES ('2', 'C语言入门视频教程02', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言是一种计算机程序设计语言，既具有高级语言的特点，又具有汇编语言的特点。今天我们同大家分享的这部C语言入门视频教程是一部非常棒的教程。欢迎感兴趣的朋友来观看学习!\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言的特点：1. C是高级语言。它把高级语言的基本结构和语句与低级语言的实用性结合起来。C \r\n语言可以像汇编语言一样对位、字节和地址进行操作，而这三者是计算机最基本的工作单元。\r\n</p>', '1386662725.jpg', 'http://video.1kejian.com/video/?24355-0-1.html', 'hong', '2013-12-10 16:05:25', '13');
INSERT INTO `clang_online` VALUES ('3', 'C语言入门视频教程03', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言是一种计算机程序设计语言，既具有高级语言的特点，又具有汇编语言的特点。今天我们同大家分享的这部C语言入门视频教程是一部非常棒的教程。欢迎感兴趣的朋友来观看学习!\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言的特点：1. C是高级语言。它把高级语言的基本结构和语句与低级语言的实用性结合起来。C \r\n语言可以像汇编语言一样对位、字节和地址进行操作，而这三者是计算机最基本的工作单元。\r\n</p>', '1386662764.jpg', 'http://video.1kejian.com/video/?24355-0-2.html', 'hong', '2013-12-10 16:06:04', '12');
INSERT INTO `clang_online` VALUES ('4', 'C语言入门视频教程04', '<p>\r\n	　C语言是一种计算机程序设计语言，既具有高级语言的特点，又具有汇编语言的特点。今天我们同大家分享的这部C语言入门视频教程是一部非常棒的教程。欢迎感兴趣的朋友来观看学习!\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言的特点：1. C是高级语言。它把高级语言的基本结构和语句与低级语言的实用性结合起来。C \r\n语言可以像汇编语言一样对位、字节和地址进行操作，而这三者是计算机最基本的工作单元。\r\n</p>', '1386662965.jpg', 'http://video.1kejian.com/video/?24355-0-3.html', null, '2013-12-10 16:09:25', null);
INSERT INTO `clang_online` VALUES ('5', 'C语言入门视频教程05', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言是一种计算机程序设计语言，既具有高级语言的特点，又具有汇编语言的特点。今天我们同大家分享的这部C语言入门视频教程是一部非常棒的教程。欢迎感兴趣的朋友来观看学习!\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言的特点：1. C是高级语言。它把高级语言的基本结构和语句与低级语言的实用性结合起来。C \r\n语言可以像汇编语言一样对位、字节和地址进行操作，而这三者是计算机最基本的工作单元。\r\n</p>', '1386662998.jpg', 'http://video.1kejian.com/video/?24355-0-4.html', null, '2013-12-10 16:09:58', null);
INSERT INTO `clang_online` VALUES ('6', 'C语言入门视频教程06', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言是一种计算机程序设计语言，既具有高级语言的特点，又具有汇编语言的特点。今天我们同大家分享的这部C语言入门视频教程是一部非常棒的教程。欢迎感兴趣的朋友来观看学习!\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;C语言的特点：1. C是高级语言。它把高级语言的基本结构和语句与低级语言的实用性结合起来。C \r\n语言可以像汇编语言一样对位、字节和地址进行操作，而这三者是计算机最基本的工作单元。\r\n</p>', '1386663029.jpg', 'http://video.1kejian.com/video/?24355-0-5.html', null, '2013-12-10 16:10:29', null);

-- ----------------------------
-- Table structure for `clang_practice`
-- ----------------------------
DROP TABLE IF EXISTS `clang_practice`;
CREATE TABLE `clang_practice` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `content` text COMMENT '内容描述',
  `type` varchar(10) DEFAULT NULL COMMENT '重点、难点、经典程序、教师解惑',
  `author` varchar(20) DEFAULT NULL COMMENT '发布者',
  `time` datetime DEFAULT NULL COMMENT '发布时间',
  `click` int(11) DEFAULT '0' COMMENT '点击量',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=37 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of clang_practice
-- ----------------------------
INSERT INTO `clang_practice` VALUES ('1', '攻克C语言学习难点', '&nbsp; &nbsp; 首先我们来介绍一下C语言，C语言是一种结构化语言，适于按模块化方式组织程序，层次十分清晰，易于调试和维护。C语言的表现能力和处理能力极强，不仅具有丰富的运算符和数据类型，便于实现各类复杂的数据结构，而且还可以直接访问内存的物理地址，进行位(bit)一级的操作。由于C语言的功能强大、应用广泛，它成为了目前软件开发的最常用的语言之一，C语言学习中的难点和需要注意的有那些呢?<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>下面我们重点来讲一讲C语言学习过程中所特有的一些知识难点。<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>算术表达式<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>C语言中的算术运算有单目运算和双目运算两种。单目运算指的是该运算符只能连接一个运算量，除了正负号外，主要有 (加1)和——(减1)，它有前缀和后缀之分，主要区别在于前缀 n表示先令n=n 1，然后取n的值，后缀n 表示先取n的值，然后令n=n 1.双目运算是指该运算符的左右两侧都必须有运算量。常见的双目运算符有： 、-、、/、%等。诸如x y、x/y、xy等运算均为双目运算。遇到双目运算时，要特别注意数据的类型问题。具体要求有以下几点：<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>1.双目运算的结果的类型必须与运算量的类型一致;<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>2.如果参与双目运算的两个运算量类型不一致，系统将自动把其中的一个进行类型转换，使两个运算量的类型一致后再进行运算。自动转换的规则是：按“向高看齐”的原则进行转换。在几个基本的数据类型中，由高到低的排列顺序为：double、int、char.<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>2000年春季考试中有一道选择题：<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>下列程序的输出结果是( )<br />\r\n　　A) 3 B) 3.2 C) 0 D) 3.07<br />\r\n　　main<br />\r\n　　 double d=3.2 int xy<br />\r\n　　x=1.2 y=x 3.8　/5.0<br />\r\n　　printf″%d&nbsp;<a href=\"file://xn--n-yina/\">\\\\n″</a> dy　解析：本题中，程序先执行语句x=1.2，根据赋值运算的类型转换规则，这里要将double型的常量1.2转换为int型，即取整为1，然后将1赋值给变量x.接下来执行语句y=x 3.8　/5.0;根据运算符的优先级，先计算小括号内，再计算除法，最后执行赋值运算。小括号内的运算过程为：先将整型变量x的值1转换为double型1.0，然后与3.8进行加法运算，得到中间结果4.8.接着进行除法运算4.8/5.0，其结果小于1.0，这里没有必要计算出精确值，因为接着进行赋值运算，赋值号左边的变量y的类型为整型，于是对这个小于1.0的中间结果进行取整，结果为0，于是变量y的值为0.因此该程序的输出结果应该是0，即正确答案为C.逗号表达式<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>C语言中的逗号表达式的形式如下：<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>表达式1，表达式2，表达式3，……，表达式n<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>学习逗号表达式时一定要掌握它的要领：1.逗号表达式的运算过程为：从左往右逐个计算;2.逗号表达式是作为一个整体的，它的最终结果就是最后一个表达式(也即表达式n)的值;3.逗号运算符的优先级别是所有运算符中最低的。只有这样，才能真正掌握这种运算，得出正确的解答。<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>2001年秋季考试中有一道选择题(此题曾在1998年春季考试中出现过)：<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>若已定义x和y为double类型，则表达式：x=1y=x 3/2的值是：<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>A) 1 B) 2 C) 2.0 D) 2.5<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>解析：该表达式就是一个逗号表达式，所以要按照运算顺序先运算x=1，所以变量x的值为1.0，然后运算y=x 3/2，此表达式中需先计算二个整数相除3/2，结果为整数1，然后再与x相加，结果为2.0.由于逗号表达式x=1y=x 3/2的值就是其最后一个表达式即y=x 3/2的值，此值即为2.0，所以正确的答案应该是C.<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>复合语句<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>复合语句就是用一对“{}”把若干语句括起来构成一个语句组，复合语句是C语言中最重要的一种语句，语句的形式如下： [Page]语句l;语句2;…;语句n<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>一个复合语句在语法上视为一条语句，在复合语句中，花括号内的语句数量不限，可以有执行语句，也可以有定义部分，定义部分应该出现在可执行语句的前面，复合语句可以嵌套使用，例如：{语句1;{语句2;…;语句n;}}.<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>复合语句还可以嵌套C语言中任何结构的控制语句，如if、switch、while、for等。使用好复合语句的关键是理解和掌握变量的作用域。', '难点', 'hong', '2013-12-09 21:30:21', '10');
INSERT INTO `clang_practice` VALUES ('2', '变量作用域和存储类别：', '&nbsp; &nbsp; 了解了基本的变量类型后，我们要进一步了解它的存储类别和变量作用域问题。<br />\r\n变量类别 子类别 局部变量 静态变量（离开函数，变量值仍保留） 自动变量寄存器变量 全局变量 静态变量（只能在本文件中用）非静态变量（允许其他文件使用）<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>换一个角度<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>变量类别 子类别 静态存储变量 静态局部变量（函数） 静态全局变量（本文件） 非静态全局/外部变量（其他文件引用） 动态存储变量 自动变量 寄存器变量 形式参数&nbsp;<br />\r\n<span>&nbsp; &nbsp;&nbsp;</span>extern型的存储变量在处理多文件问题时常能用到，在一个文件中定义extern型的变量即说明这个变量用的是其他文件的。顺便说一下，笔者在做课设时遇到out of memory的错误，于是改成做多文件，再把它include进来（注意自己写的*.h要用“”不用&lt;&gt;），能起到一定的效用。static型的在读程序写结果的试题中是个考点。多数时候整个程序会出现多个定义的变量在不同的函数中，考查在不同位置同一变量的值是多少。主要是遵循一个原则，只要本函数内没有定义的变量就用全局变量（而不是main里的），全局变量和局部变量重名时局部变量起作用，当然还要注意静态与自动变量的区别。', '难点', 'hong', '2013-12-09 21:32:17', '13');
INSERT INTO `clang_practice` VALUES ('3', '你没有学过的C语言：重定义一些基本数据类型', '<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; 在编写程序的时候，定义变量是必不可少的，这时就要用到一些基本数据类型。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; 也许你会说，定义一个变量有什么困难的，这是很容易办到的。要定义整型变量，直接用“int i;”语句就可以了；要定义字符型变量，直接用“char c;”就可以了。确实，这是学校教育教给我们的，但在实际工作中却不能这样做。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; 为什么呢？原因很简单，因为这样写出来的程序，看起来很“山寨”，不是专业的程序员应该做的事情。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; 最近，我在看《楚汉传奇》，有一个场面给我留下了非常深刻的印象。当刘邦第一次去找项羽帮忙的时候，他们一行人看到了项羽军营是如何练兵的，想到自己练兵的方法，当时就震惊了。刘邦他们完全就像是一群没有规范、到处乱跑的农民，与项家军比起来，真是差得太远了。这种差别移植到软件开发上来，就是“山寨”与“专业”的差别。这个比喻虽然有点不恰当，当也说明了一定的道理。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; 书本上学的基本数据类型就像是不懂事的小孩，只有对之进行一定的规范和训练，才能走出社会，才能为单位所用。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	根据自己的工作经验，常用到的一些基本数据类型的重定义(规范化)如下：\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; &nbsp;(1)&nbsp;整型\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; &nbsp;整型，以占数据位数来分包括了短整型(short int)、整型(int)和长整型(long int)，以有无符号来分包括了无符号整型(unsigned)和有符号整型(signed)。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	&nbsp; &nbsp; 对于无符号短整型，其重定义为：typedef unsigned short int UINT16;\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>对于有符号短整型，其重定义为：typedef signed&nbsp;&nbsp; short int INT16;\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span> 对于无符号整型，其重定义为：typedef unsigned int UINT32;\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span> 对于有符号整型，其重定义为：typedef signed&nbsp; int INT32;\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>对于无符号长整型，其重定义为：typedef unsigned long ULONG;\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span> 对于有符号长整型，其重定义为：typedef signed &nbsp;long LONG;\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>通过以上重定义之后，我们就可以用UINT16、INT16、UINT32、INT32、ULONG和LONG来代替相应的整型类型了。这里拿UINT32来说明：“U”代表“unsigned”，“INT”代表“int”，“32”表示“32位”，从这个定义一眼就可以看出这表示32位的无符号整型数据类型，如果要定义一个这样的一个变量i，用“UINT32 i;”语句就可以了。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(2)&nbsp;字符型\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>由于字符型和整型可以相互转换，也可以用类似的办法来重定义。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>对于无符号字符型，其重定义为：typedef unsigned char UINT8;\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>对于有符号字符型，其重定义为：typedef signed char INT8;\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>由于一个字符型变量占据一个字节，因此可以用8位来表示。通过我们的重定义，也能够很清楚地看出来。如果要定义一个无符号字符型变量c，用“UINT8 c;”语句就可以了。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span><span style=\"line-height:1.5;\">一般说来，实际项目中，这两种数据类型用得比较多，其它数据类型的重定义也可以参照完成。</span>\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>对基本数据类型进行重定义也就是在进行代码编写的规范化。在大型代码文件中，按照这个规范编写的程序阅读起来比较方便，同时也易于理解，提高了工作效率，让人一看到就觉得十分的专业。另外，这样做也会提升开发工程师在客户心中的地位，能够获得他们比较好的评价。\r\n</p>\r\n<p style=\"text-align:left;\">\r\n	<span>&nbsp; &nbsp;&nbsp;</span>当然，文章中的重定义符号(如INT16等)只是作者的举例，各个公司有不同的喜好和规定，因此会有不同的表示方法。但是，大家的目的都是一样的：体现规范性和专业性。\r\n</p>\r\n<span>&nbsp; &nbsp;&nbsp;</span>规范性是我们随时都要注意的事情，要在自己编写的每一行代码中体现出来。我们要不断努力，力争早日远离“山寨”。', '重点', 'hong', '2013-12-09 21:21:11', '73');
INSERT INTO `clang_practice` VALUES ('4', '有1、2、3、4个数字，能组成多少个互不相同且无重复数字的三位数？都是多少？', '1.程序分析：可填在百位、十位、个位的数字都是1、2、3、4。组成所有的排列后再去&nbsp;<br />\r\n　　　　　　掉不满足条件的排列。&nbsp;<br />\r\n2.程序源代码：<br />\r\n<br />\r\n<span style=\"line-height:1.5;\">代码如下:</span><br />\r\nmain()&nbsp;<br />\r\n{&nbsp;<br />\r\nint i,j,k;&nbsp;<br />\r\nprintf(\"\\n\");&nbsp;<br />\r\nfor(i=1;i&lt;5;i++)　　　　／*以下为三重循环*/&nbsp;<br />\r\n　for(j=1;j&lt;5;j++)　&nbsp;<br />\r\n　　for (k=1;k&lt;5;k++)&nbsp;<br />\r\n　　　{&nbsp;<br />\r\n　　　　if (i!=k&amp;&amp;i!=j&amp;&amp;j!=k) 　　　/*确保i、j、k三位互不相同*/&nbsp;<br />\r\n　　　　printf(\"%d,%d,%d\\n\",i,j,k);&nbsp;<br />\r\n　　　 }&nbsp;<br />\r\n}&nbsp;<br />\r\n<br />', '经典程序', 'hong', '2013-12-09 21:44:46', '128');
INSERT INTO `clang_practice` VALUES ('5', '输出以下的杨辉三角形（要求输出十行）：', '1<br />\r\n1 1<br />\r\n1 2 1<br />\r\n1 3 3 1<br />\r\n1 4 6 4 1<br />\r\n1 5 10 10 5 1<br />\r\n&nbsp;. . . . . . . .<br />\r\n#include&lt;stdio.h&gt;<br />\r\n#define N 11<br />\r\nvoid main()<br />\r\n{int i,j,a[N][N];<br />\r\nfor(i=1;i&lt;N;i++)<br />\r\n{a[i][1]=1;<br />\r\n&nbsp;a[i][i]=1;<br />\r\n&nbsp;}<br />\r\n&nbsp;for(i=3;i&lt;N;i++)<br />\r\nfor(j=2;j&lt;=i-1;j++)<br />\r\n&nbsp; a[i][j]=a[i-1][j-1]+a[i-1][j];<br />\r\n&nbsp;for(i=1;i&lt;N;i++)<br />\r\n{for(j=1;j&lt;=i;j++)<br />\r\n&nbsp; &nbsp;printf(“%6d”,a[i][j]);<br />\r\nprintf(“\\n”);<br />\r\n}<br />\r\n&nbsp;printf(“\\n”);<br />\r\n}<br />', '教师解惑', 'hong', '2013-12-10 09:08:44', '26');
INSERT INTO `clang_practice` VALUES ('6', '其次，一审法院认为腾讯的商业模式应当予以保护，该认定错误。商业模式本身并不构成任何法律所保护的对象，任何技术的创新和突破都会破坏旧有的商业模式；', '2011年4月，腾讯在广东高院起诉360“扣扣保镖”不正当竞争，索赔1.25亿。腾讯起诉认为，360“扣扣保镖”破坏和篡改了腾讯QQ软件的功能，同时屏蔽客户广告，破坏了腾讯商业模式。\r\n\r\n　　360方面则认为，推出“扣扣保镖”，是为了360的生存发展，也是尊重用户的选择。“扣扣保镖”是个创新型反捆绑工具软件，不涉及QQ核心聊天功能，不会触碰用户的QQ账号密码和聊天记录等；相反，“扣扣保镖”会大幅度提高QQ账号、密码、聊天记录的安全等级，工具本身无罪。\r\n\r\n　　2013年4月25日，广东省高级人法院对腾讯诉360“扣扣保镖”案做出一审判决，判处360败诉，赔偿腾讯500万元。\r\n\r\n　　360方面不服此判决，认为在“扣扣保镖”案一审中，广东法院存在很多错误的判定，并将此案上诉至最高人民法院。\r\n\r\n　　360总法律顾问傅彤此前解释说，“扣扣保镖”没有破坏QQ软件及其服务的安全性、完整性，没有使被上诉人丧失交易机会和广告收入，不构成不正当竞争，一审法院认定的事实不清、适用法律严重错误：\r\n\r\n　　首先，广东高院判决认定用户在享受QQ免费服务时必须忍受其中的广告、插件，明显不当；\r\n\r\n　　其次，一审法院认为腾讯的商业模式应当予以保护，该认定错误。商业模式本身并不构成任何法律所保护的对象，任何技术的创新和突破都会破坏旧有的商业模式；\r\n\r\n　　再次，“扣扣保镖”屏蔽QQ软件广告、插件的功能完全合法，软件的选择、安装和使用都是在用户充分知情和可以自由选择的情况下完成的，工具本身无罪，并没有违反诚实信用原则和公认的商业道德。\r\n\r\n　　同时，广东高院罔顾了几项事实：腾讯QQ在用户未知情的情况下强制扫描用户电脑文件，并强行弹窗，遭遇用户诟病；腾讯赤裸裸地抄袭360安全卫士的功能和界面，并借助它庞大的QQ用户群，强行捆绑，通过品牌混淆强占市场；腾讯官方发布《致广大QQ用户的一封信》，停止向安装了360软件的用户提供相关的软件服务，胁迫用户“二选一”，这是典型的滥用市场支配地位的行为。\r\n\r\n　　“此外，广东高院判决高达500万人民币的赔偿金，这是在没有认可腾讯的损失证据情况下，法官自由裁量的结果。这一判决金额在互联网界纠纷、名誉权类纠纷中的量级是空前的。广东高院超出了法院自由裁量权的限度，违反了公平、公正、公开的原则。 ”傅彤说。 (张楠)', '学生设计', 'hong', '2013-11-25 22:46:01', '18');
INSERT INTO `clang_practice` VALUES ('7', '程序的版式和注释', '<p>\r\n	&nbsp; &nbsp; 在《高质量程序设计指南》中，作者说：可以把程序的版式比喻为“书法”，好的“书法”可以让人对程序一目了然，看得兴致勃勃。确实，我们一打开程序，首先看到的便是程序的排版，我们的第一印象便是程序写得是工整还是凌乱。都说第一印象很重要，为了给大家留下好的第一印象，我们一定要注重程序的版式。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>此外，好的注释能够帮助读者更快地理解程序，提高工作的效率。也许是因为工作比较忙的缘故，很多软件工程师不喜欢为自己的程序写注释，认为这样做是没有必要的。那么，如果给他一份毫无注释的代码，他会有什么样的感受呢？在程序中，优美的、得当的注释能够起到锦上添花的作用，更能够体现出一个工程师的专业素质。因此，写注释实在是很有必要的。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>在C语言程序中，一般都包括了头文件(.h文件)和实现文件(.c文件)，它们的版式及注释如下：\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>第一，头文件的版式和注释。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>头文件起到一个辅助的作用，简要地反应出本程序的基本功能。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>一般说来，头文件的版式可采用以下的样式：\r\n</p>\r\n<p>\r\n	/***************************************************************\r\n</p>\r\n<p>\r\n	*&nbsp;版权所有&nbsp;(C)2013,&nbsp;公司名称。\r\n</p>\r\n<p>\r\n	*\r\n</p>\r\n<p>\r\n	*&nbsp;文件名称：\r\n</p>\r\n<p>\r\n	*&nbsp;内容摘要：\r\n</p>\r\n<p>\r\n	*&nbsp;其它说明：\r\n</p>\r\n<p>\r\n	*&nbsp;当前版本：\r\n</p>\r\n<p>\r\n	*&nbsp;作&nbsp;&nbsp;&nbsp;&nbsp;者：\r\n</p>\r\n<p>\r\n	*&nbsp;完成日期：\r\n</p>\r\n<p>\r\n	*\r\n</p>\r\n<p>\r\n	*&nbsp;修改记录1：&nbsp; //&nbsp;修改历史记录，包括修改日期、修改者及修改内容等\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改日期：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;版本号：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改人：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改内容：\r\n</p>\r\n<p>\r\n	***************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	#ifndef _XXX_H&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;防止头文件被重复引用\r\n</p>\r\n<p>\r\n	#define _XXX_H\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	/**************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 相关宏定义\r\n</p>\r\n<p>\r\n	**************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	/**************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;相关结构体定义\r\n</p>\r\n<p>\r\n	**************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	/**************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;本程序中出现的函数的声明&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n	**************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	#endif\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>说明：\r\n</p>\r\n<p>\r\n	<span><span>&nbsp; &nbsp;&nbsp;</span></span>(1)&nbsp;在头文件开始的地方，一定要有注释，不能马上开始写代码。这里的注释主要是版权和版本相关的信息。为什么需要呢？因为我们已经不在学校了，我们写的每一行代码都属于公司，所以要有版权声明，这也是对一个职业人的基本要求。\r\n</p>\r\n<p>\r\n	<span><span>&nbsp; &nbsp;&nbsp;</span></span>(2)&nbsp;红色字体所示的代码是为了防止头文件被重复引用，这在每个工程文件中都是必须的。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(3)&nbsp;为了便于理解，我们会将很多诸如数字等信息用宏来代替，就像C语言课上我们用“PI”来代表圆周率的值一样。此时，就需要在头文件中对实现文件中需要用到的宏进行定义，并给出适当的注释，方便理解。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(4)&nbsp;在实际的C语言程序，结构体是经常会用到的。例如，某个消息结构体包含了很多的字段，这时用一个结构体进行定义是很方便的。在头文件中，尽量包含本程序要用到的所有结构体。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(5)&nbsp;在学校里，很多人都不习惯对函数进行声明。为了防止函数在没有定义之前就被引用，大家一定要在头文件中对本程序中出现的所有函数进行声明。为了便于理解，在某些重要函数后面一定要写上注释。\r\n</p>\r\n<p>\r\n	&nbsp;<span style=\"line-height:1.5;\">&nbsp; &nbsp;&nbsp;</span><span style=\"line-height:1.5;\">第二，实现文件的版式和注释。</span>\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>实现文件(.c)文件是程序的核心，所有的工作都是在里面完成的。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>一般说来，实现文件的版式可采用以下的样式：\r\n</p>\r\n<p>\r\n	/***************************************************************\r\n</p>\r\n<p>\r\n	*&nbsp;版权所有&nbsp;(C)2013,&nbsp;公司名称。\r\n</p>\r\n<p>\r\n	*\r\n</p>\r\n<p>\r\n	*&nbsp;文件名称：\r\n</p>\r\n<p>\r\n	*&nbsp;内容摘要：\r\n</p>\r\n<p>\r\n	*&nbsp;其它说明：\r\n</p>\r\n<p>\r\n	*&nbsp;当前版本：\r\n</p>\r\n<p>\r\n	*&nbsp;作&nbsp;&nbsp;&nbsp;&nbsp;者：\r\n</p>\r\n<p>\r\n	*&nbsp;完成日期：\r\n</p>\r\n<p>\r\n	*\r\n</p>\r\n<p>\r\n	*&nbsp;修改记录1：&nbsp;&nbsp; //&nbsp;修改历史记录，包括修改日期、修改者及修改内容等\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改日期：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;版本号：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改人：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改内容：\r\n</p>\r\n<p>\r\n	*\r\n</p>\r\n<p>\r\n	*&nbsp;修改记录2：&nbsp; //&nbsp;修改历史记录，包括修改日期、修改者及修改内容等\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改日期：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;版本号：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改人：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改内容：\r\n</p>\r\n<p>\r\n	***************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	/**************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 头文件引用\r\n</p>\r\n<p>\r\n	**************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	/**************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 全局变量定义\r\n</p>\r\n<p>\r\n	**************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	/**************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; 函数实现\r\n</p>\r\n<p>\r\n	**************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;</span>&nbsp;说明：\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(1)&nbsp;函数头部的版权和版本相关的信息与头文件的类似，也是必不可少的。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(2)&nbsp;在实现文件的函数中，会调用很多不在该文件中定义的函数，因此，需要将这些函数包括进来。怎么办呢？就要引用定义这些函数的头文件，像我们很熟悉的“#include &lt;stdio.h&gt;”。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(3)&nbsp;当程序函数比较多时，会出现很多函数都需要引用同一个变量的情况，这就需要定义一个全局变量供它们使用。但全局变量的个数要尽量少，尽量不要定义多余的全局变量。什么原因呢？就拿人类来说，我们依靠别人越少，我们就会越自由，如果做很多事情都要别人点头，你的心里感觉如何？\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(4)&nbsp;函数实现是程序的核心，我们开头做了那么多的工作，都是为了实现函数服务的。那么函数到底应该怎么写？这是以后的内容。这里要说的是函数头部的注释。受到学校教育的影响，很多开发工程师不喜欢写注释，或者是写出很简单的注释，起不到注释应有的作用。根据作者的经验，函数头部的可采用如下的样式：\r\n</p>\r\n<p>\r\n	/**********************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;功能描述：\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;输入参数：\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;输出参数：\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;返回值：\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;其它说明：\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;修改日期&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;版本号&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修改人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修改内容\r\n</p>\r\n<p>\r\n	&nbsp;* ------------------------------------------------------------------------------------------------------\r\n</p>\r\n<p>\r\n	&nbsp;* YYYYMMDD&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;XXX&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;Name&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; YYY\r\n</p>\r\n<p>\r\n	&nbsp;***********************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 在写好注释之后，便可以开始写正式的函数实现语句了。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span><span style=\"line-height:1.5;\"></span><span style=\"line-height:1.5;\">在C语言程序中，注释的书写有两种方式，一种是用“//”来标注，一种是用“/* … */”来标注。当只需要对单行代码进行注释时，可以采用第一种方式；当对多行代码进行添加或删除时，可以采用第二种方式。当然，这只是作者个人的看法，不同的项目组有不同的规范。</span>\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span><span style=\"line-height:1.5;\">&nbsp;“千里之行，始于足下”，当我们看到排版工整、注释规范的代码时，会产生继续读下去的想法(如果当时你的心情非常不好，那就另当别论了)，这也就无形中提高了办事的效率。同时，会让他人对程序的作者产生好的第一印象，进而使得下一步的愉快合作成为可能。因此，于公于私，我们都应该注意程序的版式和注释。</span>\r\n</p>', '重点', 'hong', '2013-12-09 21:23:28', '72');
INSERT INTO `clang_practice` VALUES ('10', '变量及函数的命名', '<p>\r\n	&nbsp; &nbsp; 最近看了一则笑话，大意是路人甲到电信营业厅去交电话费，给营业员说要充50块钱，那个营业员问了一句“How much?”。路人甲心想现在社会真的与以往不同了，连一般的营业员都开始流行讲英语了，于是便以不是很流利的口语说了一句“Fifty YUAN”。那个营业员一头雾水地看着他，说道“电话号码多少？”路人甲这才恍然大悟，原来是问我电话号码多少，而非多少钱。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>也许这个笑话本身并不好笑，但却说明了一个道理：我们要尽量准确地表达自己的想法，不要让别人感到疑惑。这个原理同样适用于软件开发过程，适用于编写代码的过程。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>我们在编程过程中，要保证自己写的每一行语句都表意准确，让别人一看就能够明白，特别是对于变量和函数的命名。受学校教育的影响，诸如“int i,j;”、“char c;”之类的变量定义到处都是，严重影响了工作的效率。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>在本文中，我也不想说一些条条框框的东西，而是以一个实际的例子来为读者展示如何命名变量和函数是恰当的。这里用《你没有学过的C语言(1)：引言》(<a href=\"http://blog.csdn.net/zhouzxi/article/details/8976122\">http://blog.csdn.net/zhouzxi/article/details/8976122</a>)中出现的程序代码为例，将该代码按照规范修改后，如下所示：\r\n</p>\r\n<p>\r\n	/***************************************************************\r\n</p>\r\n<p>\r\n	*&nbsp;版权所有&nbsp;(C)2013, CSDN。\r\n</p>\r\n<p>\r\n	*\r\n</p>\r\n<p>\r\n	*&nbsp;文件名称：example.c\r\n</p>\r\n<p>\r\n	*&nbsp;内容摘要：用于示范如何给变量和函数做规范的命名\r\n</p>\r\n<p>\r\n	*&nbsp;其它说明：无\r\n</p>\r\n<p>\r\n	*&nbsp;当前版本：V1.0\r\n</p>\r\n<p>\r\n	*&nbsp;作&nbsp;&nbsp;&nbsp;&nbsp;者：周兆熊\r\n</p>\r\n<p>\r\n	*&nbsp;完成日期：20130712\r\n</p>\r\n<p>\r\n	*\r\n</p>\r\n<p>\r\n	*&nbsp;修改记录1：&nbsp;&nbsp; //&nbsp;修改历史记录，包括修改日期、修改者及修改内容等\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改日期：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;版本号：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改人：\r\n</p>\r\n<p>\r\n	*&nbsp;&nbsp;&nbsp;&nbsp;修改内容：\r\n</p>\r\n<p>\r\n	***************************************************************/\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	#include &lt;stdio.h&gt;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	typedef signed int INT32;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;重定义数据类型\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	float ProcessFactorial(INT32 iInputValue);&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;函数声明\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	/**********************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;功能描述：主函数\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;输入参数：无\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;输出参数：无\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;返回值：无\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;其它说明：无\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;修改日期&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;版本号&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修改人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修改内容\r\n</p>\r\n<p>\r\n	&nbsp;* --------------------------------------------------------------------\r\n</p>\r\n<p>\r\n	&nbsp;* 20130712&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; V1.0&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周兆熊&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;创建\r\n</p>\r\n<p>\r\n	&nbsp;***********************************************************************/\r\n</p>\r\n<p>\r\n	void main()\r\n</p>\r\n<p>\r\n	{\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; INT32 iInputValue = 0;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; float&nbsp; fResult&nbsp;&nbsp;&nbsp;&nbsp; = 0.0;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; printf(\"input an integer number: \");\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; scanf(\"%d\", &amp;iInputValue);\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; fResult = ProcessFactorial(iInputValue);&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;调用求阶乘的函数\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; printf(\"%d!=%10.0f\\n\", iInputValue, fResult);\r\n</p>\r\n<p>\r\n	}\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	/**********************************************************************\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;功能描述：求一个数的阶乘\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;输入参数：&nbsp;iInputValue-输入值\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;输出参数：无\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;返回值：求阶乘后的结果\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;其它说明：无\r\n</p>\r\n<p>\r\n	&nbsp;*&nbsp;修改日期&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;版本号&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修改人&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;修改内容\r\n</p>\r\n<p>\r\n	&nbsp;* --------------------------------------------------------------------\r\n</p>\r\n<p>\r\n	&nbsp;* 20130712&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; V1.0&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;周兆熊&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;创建\r\n</p>\r\n<p>\r\n	&nbsp;***********************************************************************/\r\n</p>\r\n<p>\r\n	float ProcessFactorial(INT32 iInputValue)\r\n</p>\r\n<p>\r\n	{\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; float&nbsp; fResult&nbsp;&nbsp;&nbsp;&nbsp; = 0.0;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; //&nbsp;先判断输入值是否小于0\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; if (iInputValue &lt; 0)\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; {\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"iInputValue &lt; 0, dataerror!\");\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return -1;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; }\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp; else\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if (iInputValue == 0 || iInputValue == 1) &nbsp;// 0和1的阶乘是1\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fResult = 1;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;fResult = ProcessFactorial(iInputValue-1)*iInputValue; &nbsp;//&nbsp;执行递归调用\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; &nbsp;&nbsp;&nbsp; }\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; return(fResult);\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }\r\n</p>\r\n<p>\r\n	}\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>该代码和之前的代码相比，是不是有很大的不同？\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>有关类型重定义、注释等相关内容，请参考我之前的文章，这里重点说一下变量和函数的命名：\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(1)&nbsp;不管是函数还是变量，它们的命名只有一个原则：让读者一眼就能够看出它们表达的意思。如ProcessFactorial函数，一看到它，就大概能够明白这是在求阶乘(如果你英语确实不好，那就另当别论了)；iInputValue变量，表示输入值；fResult变量，表示结果值。我们要让代码自己来说话，而不是要作者向别人解释半天才行。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(2)&nbsp;为了让读者看到一个变量就知道其类型，在变量的最前面，通常会有一个标识类型的字母。如iInputValue中的第一个字母“i”表示这是一个整型变量(“i”代表“int”)，fResult中的第一个字母“f”表示这是一个浮点型变量(“f”代表“float”)。这样做，也是为了更进一步让读者对代码能够了然于心，能够很容易读懂，这样也省去了作者的很多向别人解释的时间，可谓是“大家皆方便”。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(3)&nbsp;始终要明白，我们第一是为人编写代码，其次才是计算机。如果只是计算机能够读懂的代码，不是好代码，也无法体现出一个编程人员的水平。\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">&nbsp; &nbsp; 很多人认为写代码很枯燥，其实不然。如果你每天写出来的代码像谭浩强老师写的《C程序设计》中那些代码一样，当然会觉得枯燥，也许你昨天写的代码，今天就不知道它们是什么意思了。但如果你时刻牢记编程规则，写出“自己能够说话的代码”(即变量和函数的命名很恰当)，那么你就不会觉得这份工作很枯燥，反而会觉得它很有趣。这可能就是所谓的“编程境界”吧。</span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\">&nbsp; &nbsp; &nbsp;“熟能生巧”，只有通过不断地练习，我们才能够懂得编写代码的诀窍，也才能够写得出好的、高质量的代码。</span>\r\n</p>', '重点', null, '2013-12-09 21:25:40', '1');
INSERT INTO `clang_practice` VALUES ('11', '既要写好代码，又要写好文档', '<p>\r\n	&nbsp; &nbsp; 看到这个标题，读者也许会问：“是不是偏题了？我们不是在讲C语言的东东吗？”讲C语言是不假，但这也没有偏题。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>对于软件相关行业，在学校或单位上，大家也许都已经注意到了，除了要编写的程序、绘制设计图之外，还有一个重要的工作便是写文档。为什么要写文档呢？因为我们要把自己做的东西展示出来，不光展示给同行看，可能还要展示给其他岗位上的工作人员看，甚至展示给用户看。如果我们只是会写程序，不会在文档中描述自己的想法，那么就真正的成为“码农”了。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>工作也有一年了，我发现周围的同事，会写高质量文档的确实很少。李开复老师在《浪潮之巅》的序言中说到：“我认识很多顶尖的工程师，但具备强大叙事能力的优秀工程师，我认识的可以说是凤毛麟角。”确实，我所认识的同事，能够清晰表达自己思想的人也很少。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>有关文档书写，我印象很深的有如下几个方面：\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(1)&nbsp;我们每天都会收发很多邮件，我仔细看了一下，很多邮件里面的内容要么语句不通顺、要么有很多错别字、要么误用或没有标点符号。很多时候，从不同的角度理解，一封邮件有很多不同的意思，让人感觉不知道它究竟要表达一个什么意思，这样极大地降低了工作的效率。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(2)&nbsp;除了代码之外，项目也会包含了大量的文档。打开大部分文档，看到的第一眼，我就有这几种感觉：排版不工整、格式不正确、语句不通顺、错别字连篇。一看就知道作者没有认真写文档，并且语句的表达和组织能力也不强。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(3)&nbsp;在项目小组成员讨论的时候，大家几乎都在说怎样把程序写好，而没有提到在文档书写方面应如何努力去改进。大家一直认为开发人员的职责就是把程序写好，其它什么的都是其次的。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>有关计算机软件的传统定义为：软件是计算机系统中与硬件相依存的另一部分，软件包括程序、数据及其相关文档的完整集合。注意，这里面就提到了“相关文档”，如果文档没有写好，那么软件也不能算是优秀的软件。事实上，软件功能健全，而由于文档原因出现故障的情况还时有发生。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>一般说来，在软件开发过程中，不同阶段涉及到的主要文档如下图所示：\r\n</p>\r\n<p>\r\n	<img src=\"http://img.blog.csdn.net/20130721112416656?watermark/2/text/aHR0cDovL2Jsb2cuY3Nkbi5uZXQvemhvdXp4aQ==/font/5a6L5L2T/fontsize/400/fill/I0JBQkFCMA==/dissolve/70/gravity/SouthEast\" alt=\"\" />\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>可见，在软件的不同阶段，需要编写不同的文档。在设计阶段，需要编写详细设计文档、单元测试方案文档、集成测试方案文档等；在开发阶段，也是这几个文档，不过是修订版，因为我们在实际开发过程中，会发现之前设计不合理的地方或者是考虑不周的地方，这就需要对之前的文档进行修改；在测试阶段，要编写单元测试报告、集成测试报告等；在软件的发布阶段，要编写安装手册、用户手册、升级指导书等，这些文档主要是面向现场支持人员和用户的，因此要尽量写得通俗易懂，千万不要有模棱两可的情况存在，否则就只有等待用户的投诉了。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>要想写好文档，我们需要首先纠正一个观念：文档不重要。要把文档放在与程序同等重要的位置。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>那么，我们如何才能写出高质量的文档呢？我认为可以从如下几个方面着手：\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(1)&nbsp;改变文档为辅的观念，在平常的工作中，对于自己编写的每一份文档，均认真对待。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(2)&nbsp;对于邮件的编写，要把自己想说的话准确地表达出来，在发送邮件之前，再看一下内容是否完整、是否还有错别字、语句是否通顺等。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(3)在编写文档的过程中，要严格参照项目组规定的模板来完成。在写完文档之后，对文档进行语法检查，以纠正错别字和有语法错误的地方。一般说来，有语法错误的语句下面会有一条绿色的波浪线。在提交文档之前，再通读一下整个文档，看是否还有疏漏和不足。\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>(4)&nbsp;在工作之余，可以读一些能够提高语言表达能力和写作能力的书籍，看一下别人是怎么表达自己思想的。在此，推荐阅读吴军的《浪潮之巅》(<a href=\"http://book.douban.com/subject/24738302/\">http://book.douban.com/subject/24738302/</a>)和《数学之美》(<a href=\"http://book.douban.com/subject/10750155/\">http://book.douban.com/subject/10750155/</a>)，以及刘未鹏的《暗时间》(<a href=\"http://book.douban.com/subject/6709809/\">http://book.douban.com/subject/6709809/</a>)，这几本书写得都比较好。\r\n</p>\r\n<p>\r\n	&nbsp;\r\n</p>\r\n<p>\r\n	<span>&nbsp; &nbsp;&nbsp;</span>总的说来，和做其它事情一样，书写文档也反映了一个人的态度问题。写出高质量的文档，不仅可以提升个人的形象(如果你看到一篇好文档，是不是也对作者有较高的评价？)，还能够提升产品在客户心中的形象。如此分析，多花些心思来书写文档真的是很有必要。\r\n</p>\r\n<span>&nbsp; &nbsp;&nbsp;</span>“雄关漫道真如铁，而今迈步从头越”，要想做好一件事情，需要我们从各个方面来努力。在开发软件的过程中，写好代码很重要，清晰地表达自己思想同样非常的重要。“代码”和“文档”就像是一个人的左膀右臂，一定要让两者均衡发展，而不能够只顾其一。', '重点', null, '2013-12-09 21:27:26', '1');
INSERT INTO `clang_practice` VALUES ('12', '函数', '&nbsp; &nbsp; 对于函数最基本的理解是从那个叫main的单词开始的，一开始总会觉得把语句一并写在main里不是挺好的么，为什么偏择出去。其实这是因为对函数还不够熟练，否则函数的运用会给我们编程带来极大的便利。我们要知道函数的返回值类型，参数的类型，以及调用函数时的形式。事先的函数说明也能起到一个提醒的好作用。所谓形参和实参，即在调用函数时写在括号里的就是实参，函数本身用的就是形参，在画流程图时用平行四边形表示传参。<br />\r\n&nbsp; &nbsp; 函数的另一个应用例子就是递归了，笔者开始比较头疼的问题，反应总是比较迟钝，按照老师的方法，把递归的过程耐心准确的逐级画出来，学习的效果还是比较好的，会觉得这种递归的运用是挺巧的，事实上，著名的八皇后、汉诺塔等问题都用到了递归。<br />\r\n例子：<br />\r\nlong fun(int n)<br />\r\n{<br />\r\nlong s;<br />\r\nif(n==1||n==2) s=2;<br />\r\n&nbsp;&nbsp;&nbsp; else s=n-fun(n-1);<br />\r\nreturn s;<br />\r\n}<br />\r\nmain()<br />\r\n{<br />\r\nprintf(\"%ld\",fun(4));<br />\r\n}&nbsp;', '难点', null, '2013-12-09 21:33:01', '1');
INSERT INTO `clang_practice` VALUES ('13', '数组', '&nbsp; &nbsp; 分为一维数组和多维数组，其存储方式画为表格的话就会一目了然，其实就是把相同类型的变量有序的放在一起。因此，在处理比较多的数据时（这也是大多数的情况）数组的应用范围是非常广的。<br />\r\n&nbsp; &nbsp; 具体的实际应用不便举例，而且绝大多数是与指针相结合的，笔者个人认为学习数组在更大程度上是为学习指针做一个铺垫。作为基础的基础要明白几种基本操作：即数组赋值、打印、排序（冒泡排序法和选择排序法）、查找。这些都不可避免的用到循环，如果觉得反应不过来，可以先一点点的把循环展开，就会越来越熟悉，以后自己编写一个功能的时候就会先找出内在规律，较好的运用了。另外数组做参数时，一维的[]里可以是空的，二维的第一个[]里可以是空的但是第二个[]中必须规定大小。<br />\r\n&nbsp; &nbsp; 冒泡法排序函数：<br />\r\nvoid bubble(int a[],int n)<br />\r\n{<br />\r\nint i,j,k;<br />\r\nfor(i=1,i&lt;n;i++)<br />\r\n&nbsp;&nbsp;&nbsp; for(j=0;j&lt;n-i-1;j++)<br />\r\n&nbsp;&nbsp;&nbsp; if(a[j]&gt;a[j+1])<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp; k=a[j];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; a[j]=a[j+1];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; a[j+1]=k;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n}<br />\r\n<br />\r\n&nbsp; &nbsp;&nbsp;选择法排序函数：<br />\r\nvoid sort(int a[],int n)<br />\r\n{<br />\r\nint i,j,k,t;<br />\r\nfor(i=0,i&lt;n-1;i++)<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; k=i;<br />\r\n&nbsp;&nbsp;&nbsp; for(j=i+1;j&lt;n;j++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(a[k]&lt;a[j]) k=j;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(k!=i)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; t=a;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; a=a[k];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; a[k]=t;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp; }&nbsp;<br />\r\n}<br />\r\n<br />\r\n&nbsp; &nbsp; 折半查找函数（原数组有序）：<br />\r\nvoid search(int a[],int n,int x)<br />\r\n{<br />\r\nint left=0,right=n-1,mid,flag=0;<br />\r\nwhile((flag==0)&amp;&amp;(left&lt;=right))<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; mid=(left+right)/2;<br />\r\n&nbsp;&nbsp;&nbsp; if(x==a[mid])<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; printf(\"%d%d\",x,mid);<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; flag =1;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else if(x&lt;a[mid]) right=mid-1;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else left=mid+1;<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\n}&nbsp;<br />\r\n&nbsp; &nbsp; 相关常用的算法还有判断回文，求阶乘，Fibanacci数列，任意进制转换，杨辉三角形计算等等。', '难点', null, '2013-12-09 21:33:50', '1');
INSERT INTO `clang_practice` VALUES ('14', '字符串', '&nbsp; &nbsp; 字符串其实就是一个数组（指针），在scanf的输入列中是不需要在前面加“&amp;”符号的，因为字符数组名本身即代表地址。值得注意的是字符串末尾的‘’，如果没有的话，字符串很有可能会不正常的打印。另外就是字符串的定义和赋值问题了，笔者有一次的比较综合的上机作业就是字符串打印老是乱码，上上下下找了一圈问题，最后发现是因为<br />\r\nchar *name;&nbsp;<br />\r\n而不是<br />\r\nchar name[10];&nbsp;<br />\r\n前者没有说明指向哪儿，更没有确定大小，导致了乱码的错误，印象挺深刻的。&nbsp;<br />\r\n另外，字符串的赋值也是需要注意的，如果是用字符指针的话，既可以定义的时候赋初值，即<br />\r\nchar *a=\"Abcdefg\";&nbsp;<br />\r\n也可以在赋值语句中赋值，即<br />\r\nchar *a;<br />\r\na=\"Abcdefg\";&nbsp;<br />\r\n<p>\r\n	&nbsp; &nbsp; 但如果是用字符数组的话，就只能在定义时整体赋初值，即char a[5]={\"abcd\"};而不能在赋值语句中整体赋值。&nbsp;\r\n</p>\r\n<p>\r\n	&nbsp; &nbsp; 常用字符串函数列表如下，要会自己实现：\r\n</p>', '难点', null, '2013-12-09 21:34:58', '1');
INSERT INTO `clang_practice` VALUES ('15', '指针', '&nbsp; &nbsp; 指针可以说是C语言中最关键的地方了，其实这个“指针”的名字对于这个概念的理解是十分形象的。首先要知道，指针变量的值（即指针变量中存放的值）是指针（即地址）。指针变量定义形式中：基本类型 *指针变量名 中的“*”代表的是这是一个指向该基本类型的指针变量，而不是内容的意思。在以后使用的时候，如*ptr=a时，“*”才表示ptr所指向的地址里放的内容是a。<br />\r\n指针比较典型又简单的一应用例子是两数互换，看下面的程序，<br />\r\nswap(int c,int d)<br />\r\n{<br />\r\nint t;<br />\r\nt=c;<br />\r\nc=d;<br />\r\nd=t;<br />\r\n}<br />\r\nmain()<br />\r\n{<br />\r\nint a=2,b=3;<br />\r\nswap(a,b);<br />\r\nprintf(“%d,%d”,a,b);<br />\r\n}&nbsp;<br />\r\n&nbsp; &nbsp; 这是不能实现a和b的数值互换的，实际上只是形参在这个函数中换来换去，对实参没什么影响。现在，用指针类型的数据做为参数的话，更改如下：<br />\r\nswap(#3333FF *p1,int *p2)<br />\r\n{<br />\r\nint t;<br />\r\nt=*p1;<br />\r\n*p1=*p2;<br />\r\n*p2=t;<br />\r\n}<br />\r\nmain()<br />\r\n{<br />\r\nint a=2,b=3;<br />\r\nint *ptr1,*ptr2;<br />\r\nptr1=&amp;a;<br />\r\nptr2=&amp;b;<br />\r\nswap(prt1,ptr2);<br />\r\nprintf(“%d,%d”,a,b);<br />\r\n}&nbsp;<br />\r\n&nbsp; &nbsp; 这样在swap中就把p1,p2 的内容给换了，即把a，b的值互换了。<br />\r\n&nbsp; &nbsp;&nbsp;指针可以执行增、减运算，结合++运算符的法则，我们可以看到:<br />\r\n*++s&nbsp;取指针变量加1以后的内容<br />\r\n*s++取指针变量所指内容后s再加1(*s)++指针变量指的内容加1<br />\r\n&nbsp; &nbsp; 指针和数组实际上几乎是一样的，数组名可以看成是一个常量指针，一维数组中ptr=&amp;b[0]则下面的表示法是等价的：<br />\r\na[3]等价于*(a+3)<br />\r\nptr[3]等价于*(ptr+3)&nbsp;<br />\r\n&nbsp; &nbsp; 下面看一个用指针来自己实现atoi（字符串型-&gt;整型）函数：<br />\r\nint atoi(char *s)<br />\r\n{<br />\r\nint sign=1,m=0;<br />\r\nif(*s==\'+\'||*s==\'-\') /*判断是否有符号*/<br />\r\nsign=(*s++==\'+\')?1:-1; /*用到三目运算符*/<br />\r\nwhile(*s!=\'\') /*对每一个字符进行操作*/<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; m=m*10+(*s-\'0\');<br />\r\n&nbsp;&nbsp;&nbsp; s++; /*指向下一个字符*/<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\nreturn m*sign;<br />\r\n}&nbsp;<br />\r\n&nbsp; &nbsp; 指向多维数组的指针变量也是一个比较广泛的运用。例如数组a[3][4]，a代表的实际是整个二维数组的首地址，即第0行的首地址，也就是一个指针变量。而a+1就不是简单的在数值上加上1了，它代表的不是a[0][1]，而是第1行的首地址，&amp;a[1][0]。<br />\r\n指针变量常用的用途还有把指针作为参数传递给其他函数，即指向函数的指针。<br />\r\n看下面的几行代码：<br />\r\nvoid Input(ST *);<br />\r\nvoid Output(ST *);<br />\r\nvoid Bubble(ST *);<br />\r\nvoid Find(ST *);<br />\r\nvoid Failure(ST *);<br />\r\n/*函数声明：这五个函数都是以一个指向ST型（事先定义过）结构的指针变量作为参数，无返回值。*/<br />\r\n<br />\r\nvoid (*process[5])(ST *)={Input,Output,Bubble,Find,Failure};<br />\r\n/*process被调用时提供5种功能不同的函数共选择(指向函数的指针数组）*/<br />\r\n<br />\r\nprintf(\"\\nChoose:\\n?\");<br />\r\nscanf(\"%d\",&amp;choice);<br />\r\nif(choice&gt;=0&amp;&amp;choice&lt;=4)<br />\r\n(*process[choice])(a); /*调用相应的函数实现不同功能*;/&nbsp;<br />\r\n&nbsp; &nbsp;&nbsp;总之，指针的应用是非常灵活和广泛的，不是三言两语能说完的，上面几个小例子只是个引子，实际编程中，会逐渐发现运用指针所能带来的便利和高效率。&nbsp;', '难点', null, '2013-12-09 21:35:57', '1');
INSERT INTO `clang_practice` VALUES ('16', '文件', '&nbsp; &nbsp; 函数调用形式&nbsp;说明fopen(\"路径\",\"打开方式\")打开文件fclose(FILE *)防止之后被误用fgetc(FILE *)从文件中读取一个字符fputc(ch,FILE *)把ch代表的字符写入这个文件里fgets(FILE *)从文件中读取一行fputs(FILE *)把一行写入文件中fprintf(FILE *,\"格式字符串\",输出表列）把数据写入文件fscanf(FILE *,\"格式字符串\",输入表列）从文件中读取fwrite（地址，sizeof（），n，FILE *）把地址中n个sizeof大的数据写入文件里fread（地址，sizeof（），n，FILE *）把文件中n个sizeof大的数据读到地址里rewind（FILE *）把文件指针拨回到文件头fseek（FILE *，x，0/1/2）移动文件指针。第二个参数是位移量，0代表从头移，1代表从当前位置移，2代表从文件尾移。feof(FILE *)判断是否到了文件末尾<br />\r\n&nbsp; &nbsp; 文件打开方式&nbsp;说明r打开只能读的文件w建立供写入的文件，如果已存在就抹去原有数据a打开或建立一个把数据追加到文件尾的文件r+打开用于更新数据的文件w+建立用于更新数据的文件，如果已存在就抹去原有数据a+打开或建立用于更新数据的文件，数据追加到文件尾<br />\r\n&nbsp; &nbsp; 注：以上用于文本文件的操作，如果是二进制文件就在上述字母后加“b”。<br />\r\n&nbsp; &nbsp;&nbsp;我们用文件最大的目的就是能让数据保存下来。因此在要用文件中数据的时候，就是要把数据读到一个结构（一般保存数据多用结构，便于管理）中去，再对结构进行操作即可。例如，文件aa.data中存储的是30个学生的成绩等信息，要遍历这些信息，对其进行成绩输出、排序、查找等工作时，我们就把这些信息先读入到一个结构数组中，再对这个数组进行操作。如下例：<br />\r\n#include&lt;stdio.h&gt;<br />\r\n#include&lt;stdlib.h&gt;<br />\r\n#define N 30 typedef struct student /*定义储存学生成绩信息的数组*/<br />\r\n{<br />\r\nchar *name;<br />\r\nint chinese;<br />\r\nint maths;<br />\r\nint phy;<br />\r\nint total;<br />\r\n}ST;<br />\r\nmain()<br />\r\n{<br />\r\nST a[N]; /*存储N个学生信息的数组*/<br />\r\nFILE *fp;<br />\r\nvoid (*process[3])(ST *)={Output,Bubble,Find}; /*实现相关功能的三个函数*/<br />\r\nint choice,i=0;<br />\r\nShow();<br />\r\nprintf(\"\\nChoose:\\n?\");<br />\r\nscanf(\"%d\",&amp;choice);<br />\r\nwhile(choice&gt;=0&amp;&amp;choice&lt;=2)<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; fp=fopen(\"aa.dat\",\"rb\");&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp; for(i=0;i&lt;N;i++)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; fread(&amp;a,sizeof(ST),1,fp); /*把文件中储存的信息逐个读到数组中去*/<br />\r\n&nbsp;&nbsp;&nbsp; fclose(fp);<br />\r\n&nbsp;&nbsp;&nbsp; (*process[choice])(a); /*前面提到的指向函数的指针，选择操作*/<br />\r\n&nbsp;&nbsp;&nbsp; printf(\"\\n\");<br />\r\n&nbsp;&nbsp;&nbsp; Show();<br />\r\n&nbsp;&nbsp;&nbsp; printf(\"\\n?\");<br />\r\n&nbsp;&nbsp;&nbsp; scanf(\"%d\",&amp;choice);<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\n}<br />\r\nvoid Show()<br />\r\n{<br />\r\nprintf(\"\\n****Choices:****\\n0.Display the data form\\n1.Bubble it according to the total score\\n2.Search\\n3.Quit!\\n\");<br />\r\n}<br />\r\nvoid Output(ST *a) /*将文件中存储的信息逐个输出*/<br />\r\n{<br />\r\nint i,t=0;<br />\r\nprintf(\"Name Chinese Maths Physics Total\\n\");<br />\r\nfor(i=0;i&lt;N;i++)<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; t=a.chinese+a.maths+a.phy;<br />\r\n&nbsp;&nbsp;&nbsp; a.total=t;<br />\r\n&nbsp;&nbsp;&nbsp; printf(\"%4s%8d%8d%8d%8d\\n\",a.name,a.chinese,a.maths,a.phy,a.total);<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\n}<br />\r\nvoid Bubble(ST *a) /*对数组进行排序，并输出结果*/<br />\r\n{<br />\r\nint i,pass;<br />\r\nST m;<br />\r\nfor(pass=0;pass&lt;N-1;pass++)<br />\r\n&nbsp;&nbsp;&nbsp; for(i=0;i&lt;N-1;i++)&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if(a.total&lt;a[i+1].total)<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; m=a; /*结构互换*/<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; a=a[i+1];<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; a[i+1]=m;&nbsp;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br />\r\nOutput(a);<br />\r\n}<br />\r\nvoid Find(ST *a)<br />\r\n{<br />\r\nint i,t=1;<br />\r\nchar m[20];<br />\r\nprintf(\"\\nEnter the name you want:\");<br />\r\nscanf(\"%s\",m);<br />\r\nfor(i=0;i&lt;N;i++)<br />\r\n&nbsp;&nbsp;&nbsp; if(!strcmp(m,a.name)) /*根据姓名匹配情况输出查找结果*/<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; printf(\"\\nThe result is:\\n%s, Chinese:%d, Maths:%d,&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Physics:%d,Total:%d\\n\",m,a.chinese,a.maths,a.phy,a.total);<br />\r\n&nbsp;&nbsp;&nbsp; t=0;<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\nif(t)<br />\r\n&nbsp;&nbsp;&nbsp; printf(\"\\nThe name is not in the list!\\n\");<br />\r\n}<br />\r\n<br />\r\n链表：<br />\r\n链表是C语言中另外一个难点。牵扯到结点、动态分配空间等等。用结构作为链表的结点是非常适合的，例如：<br />\r\nstruct node&nbsp;<br />\r\n{<br />\r\nint data;<br />\r\nstruct node *next;<br />\r\n};&nbsp;<br />\r\n其中next是指向自身所在结构类型的指针，这样就可以把一个个结点相连，构成链表。<br />\r\n链表结构的一大优势就是动态分配存储，不会像数组一样必须在定义时确定大小，造成不必要的浪费。用malloc和free函数即可实现开辟和释放存储单元。其中，malloc的参数多用sizeof运算符计算得到。<br />\r\n链表的基本操作有：正、反向建立链表；输出链表；删除链表中结点；在链表中插入结点等等，都是要熟练掌握的，初学者通过画图的方式能比较形象地理解建立、插入等实现的过程。<br />\r\ntypedef struct node<br />\r\n{<br />\r\nchar data;<br />\r\nstruct node *next;<br />\r\n}NODE; /*结点*/<br />\r\n<br />\r\n正向建立链表：<br />\r\nNODE *create()<br />\r\n{<br />\r\nchar ch=\'a\';<br />\r\nNODE *p,*h=NULL,*q=NULL;<br />\r\nwhile(ch&lt;\'z\')<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; p=(NODE *)malloc(sizeof(NODE)); /*强制类型转换为指针*/<br />\r\n&nbsp;&nbsp;&nbsp; p-&gt;data=ch;<br />\r\n&nbsp;&nbsp;&nbsp; if(h==NULL) h=p;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else q-&gt;next=p;<br />\r\n&nbsp;&nbsp;&nbsp; ch++;<br />\r\n&nbsp;&nbsp;&nbsp; q=p;<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\nq-&gt;next=NULL; /*链表结束*/<br />\r\nreturn h;<br />\r\n}&nbsp;<br />\r\n<br />\r\n逆向建立：<br />\r\nNODE *create()<br />\r\n{<br />\r\nchar ch=\'a\';<br />\r\nNODE *p,*h=NULL;<br />\r\nwhile(ch&lt;=\'z\')<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; p=(NODE *)malloc(sizeof(NODE));<br />\r\n&nbsp;&nbsp;&nbsp; p-&gt;data=ch;<br />\r\n&nbsp;&nbsp;&nbsp; p-&gt;next=h; /*不断地把head往前挪*/<br />\r\n&nbsp;&nbsp;&nbsp; h=p;<br />\r\n&nbsp;&nbsp;&nbsp; ch++;<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\nreturn h;<br />\r\n}&nbsp;<br />\r\n<br />\r\n用递归实现链表逆序输出：<br />\r\nvoid output(NODE *h)<br />\r\n{<br />\r\nif(h!=NULL)<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; output(h-&gt;next);<br />\r\n&nbsp;&nbsp;&nbsp; printf(\"%c\",h-&gt;data);<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\n}&nbsp;<br />\r\n插入结点（已有升序的链表）：<br />\r\nNODE *insert(NODE *h,int x)<br />\r\n{<br />\r\nNODE *new,*front,*current=h;<br />\r\nwhile(current!=NULL&amp;&amp;(current-&gt;data&lt;x)) /*查找插入的位置*/<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; front=current;<br />\r\n&nbsp;&nbsp;&nbsp; current=current-&gt;next;<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\nnew=(NODE *)malloc(sizeof(NODE));<br />\r\nnew-&gt;data=x;<br />\r\nnew-&gt;next=current;<br />\r\nif(current==h) /*判断是否是要插在表头*/<br />\r\n&nbsp;&nbsp;&nbsp; h=new;<br />\r\nelse front-&gt;next=new;<br />\r\nreturn h;<br />\r\n}&nbsp;<br />\r\n<br />\r\n删除结点：<br />\r\nNODE *delete(NODE *h,int x)<br />\r\n{<br />\r\nNODE *q,*p=h;<br />\r\nwhile(p!=NULL&amp;&amp;(p-&gt;data!=x))<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; q=p;<br />\r\n&nbsp;&nbsp;&nbsp; p=p-&gt;next;<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\nif(p-&gt;data==x) /*找到了要删的结点*/<br />\r\n&nbsp;&nbsp;&nbsp; {<br />\r\n&nbsp;&nbsp;&nbsp; if(p==h) /*判断是否要删表头*/<br />\r\n&nbsp;&nbsp;&nbsp; h=h-&gt;next;<br />\r\n&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; else q-&gt;next=p-&gt;next;<br />\r\n&nbsp;&nbsp;&nbsp; free(p); /*释放掉已删掉的结点*/<br />\r\n&nbsp;&nbsp;&nbsp; }<br />\r\nreturn h;<br />\r\n}&nbsp;<br />\r\n<br />\r\n经常有链表相关的程序填空题，做这样的题要注意看下面提到的变量是否定义了，用到的变量是否赋初值了，是否有给分配空间的没有分配空间，最后看看返回值是否正确。<br />\r\n笔者水平有限，难免有疏漏、错误的地方，浅显之处，还望指正见谅。上述内容仅是个提示作用，并不包括C语言的全部内容。', '难点', null, '2013-12-09 21:37:37', null);
INSERT INTO `clang_practice` VALUES ('20', '有一对兔子，从出生后第3个月起每个月都生一对兔子，小兔子长到第三个月  后每个月又生一对兔子，假如兔子都不死，问每个月的兔子总数为多少？', '1.程序分析： 兔子的规律为数列1,1,2,3,5,8,13,21....&nbsp;<br />\r\n2.程序源代码：&nbsp;<br />\r\nmain()&nbsp;<br />\r\n{&nbsp;<br />\r\nlong f1,f2;&nbsp;<br />\r\nint i;&nbsp;<br />\r\nf1=f2=1;&nbsp;<br />\r\nfor(i=1;i&lt;=20;i++)&nbsp;<br />\r\n{ printf(\"%12ld %12ld\",f1,f2);&nbsp;<br />\r\nif(i%2==0) printf(\"\\n\");/*控制输出，每行四个*/&nbsp;<br />\r\nf1=f1+f2; /*前两个月加起来赋值给第三个月*/&nbsp;<br />\r\nf2=f1+f2; /*前两个月加起来赋值给第三个月*/&nbsp;<br />\r\n}&nbsp;<br />\r\n}&nbsp;', '经典程序', null, '2013-12-09 21:53:45', '3');
INSERT INTO `clang_practice` VALUES ('21', '判断101-200之间有多少个素数，并输出所有素数。', '1.程序分析：判断素数的方法：用一个数分别去除2到sqrt(这个数)，如果能被整除，&nbsp;<br />\r\n则表明此数不是素数，反之是素数。&nbsp;<br />\r\n2.程序源代码：&nbsp;<br />\r\n#include \"math.h\"&nbsp;<br />\r\nmain()&nbsp;<br />\r\n{&nbsp;<br />\r\nint m,i,k,h=0,leap=1;&nbsp;<br />\r\nprintf(\"\\n\");&nbsp;<br />\r\nfor(m=101;m&lt;=200;m++)&nbsp;<br />\r\n{ k=sqrt(m+1);&nbsp;<br />\r\nfor(i=2;i&lt;=k;i++)&nbsp;<br />\r\nif(m%i==0)&nbsp;<br />\r\n{leap=0;break;}&nbsp;<br />\r\nif(leap) {printf(\"%-4d\",m);h++;&nbsp;<br />\r\nif(h%10==0)&nbsp;<br />\r\nprintf(\"\\n\");&nbsp;<br />\r\n}&nbsp;<br />\r\nleap=1;&nbsp;<br />\r\n}&nbsp;<br />\r\nprintf(\"\\nThe total is %d\",h);&nbsp;<br />\r\n}&nbsp;', '经典程序', null, '2013-12-09 21:54:47', '1');
INSERT INTO `clang_practice` VALUES ('22', '打印出所有的“水仙花数”，所谓“水仙花数”是指一个三位数，其各位数字立方和等于该数 本身。例如：153是一个“水仙花数”，因为153=1的三次方＋5的三次方＋3的三次方。', '1.程序分析：利用for循环控制100-999个数，每个数分解出个位，十位，百位。&nbsp;<br />\r\n2.程序源代码：&nbsp;<br />\r\nmain()&nbsp;<br />\r\n{&nbsp;<br />\r\nint i,j,k,n;&nbsp;<br />\r\nprintf(\"\'water flower\'number is:\");&nbsp;<br />\r\nfor(n=100;n&lt;1000;n++)&nbsp;<br />\r\n{&nbsp;<br />\r\ni=n/100;/*分解出百位*/&nbsp;<br />\r\nj=n/10%10;/*分解出十位*/&nbsp;<br />\r\nk=n%10;/*分解出个位*/&nbsp;<br />\r\nif(i*100+j*10+k==i*i*i+j*j*j+k*k*k)&nbsp;<br />\r\n{&nbsp;<br />\r\nprintf(\"%-5d\",n);&nbsp;<br />\r\n}&nbsp;<br />\r\n}&nbsp;<br />\r\nprintf(\"\\n\");&nbsp;<br />\r\n}&nbsp;', '经典程序', null, '2013-12-09 21:55:45', '1');
INSERT INTO `clang_practice` VALUES ('23', '输入一行字符，分别统计出其中英文字母、空格、数字和其它字符的个数。', '1.程序分析：利用while语句,条件为输入的字符不为\'\\n\'.&nbsp;<br />\r\n2.程序源代码：&nbsp;<br />\r\n#include \"stdio.h\"&nbsp;<br />\r\nmain()&nbsp;<br />\r\n{char c;&nbsp;<br />\r\nint letters=0,space=0,digit=0,others=0;&nbsp;<br />\r\nprintf(\"please input some characters\\n\");&nbsp;<br />\r\nwhile((c=getchar())!=\'\\n\')&nbsp;<br />\r\n{&nbsp;<br />\r\nif(c&gt;=\'a\'&amp;&amp;c&lt;=\'z\'||c&gt;=\'A\'&amp;&amp;c&lt;=\'Z\')&nbsp;<br />\r\nletters++;&nbsp;<br />\r\nelse if(c==\' \')&nbsp;<br />\r\nspace++;&nbsp;<br />\r\nelse if(c&gt;=\'0\'&amp;&amp;c&lt;=\'9\')&nbsp;<br />\r\ndigit++;&nbsp;<br />\r\nelse&nbsp;<br />\r\nothers++;&nbsp;<br />\r\n}&nbsp;<br />\r\nprintf(\"all in all:char=%d space=%d digit=%d others=%d\\n\",letters,&nbsp;<br />\r\nspace,digit,others);&nbsp;<br />\r\n}&nbsp;', '经典程序', null, '2013-12-09 21:56:39', '2');
INSERT INTO `clang_practice` VALUES ('24', '一球从100米高度自由落下，每次落地后反跳回原高度的一半；再落下，求它在  第10次落地时，共经过多少米？第10次反弹多高？', '1.程序分析：见下面注释&nbsp;<br />\r\n2.程序源代码：&nbsp;<br />\r\nmain()&nbsp;<br />\r\n{&nbsp;<br />\r\nfloat sn=100.0,hn=sn/2;&nbsp;<br />\r\nint n;&nbsp;<br />\r\nfor(n=2;n&lt;=10;n++)&nbsp;<br />\r\n{&nbsp;<br />\r\nsn=sn+2*hn;/*第n次落地时共经过的米数*/&nbsp;<br />\r\nhn=hn/2; /*第n次反跳高度*/&nbsp;<br />\r\n}&nbsp;<br />\r\nprintf(\"the total of road is %f\\n\",sn);&nbsp;<br />\r\nprintf(\"the tenth is %f meter\\n\",hn);&nbsp;<br />\r\n}&nbsp;', '经典程序', null, '2013-12-09 21:57:13', '1');
INSERT INTO `clang_practice` VALUES ('26', '输入三角形的边长，求三角形面积从键盘输入一个大写字母，要求改用小写字母输出', '#include&lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{<br />\r\nchar c1,c2;<br />\r\nc1=getchar();<br />\r\nprintf(“%c,%d\\n”,c1,c2);<br />\r\nc2=c1+32；<br />\r\nprintf(%c,%d\\n”,c2,c2)；<br />\r\n}<br />', '学生设计', '野狼部落', '2013-12-10 08:42:31', '2');
INSERT INTO `clang_practice` VALUES ('27', '输入两个实数，按代数值由小到大的顺序输出这两个数。', '#include&lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{<br />\r\nfloat a,b,t;<br />\r\nscanf(“%f,%f”,&amp;a,&amp;b);<br />\r\nif(a&gt;b)<br />\r\n{<br />\r\nt=a;<br />\r\na=b;<br />\r\nb=t;<br />\r\n}<br />\r\nprintf(“%5.2f,%5.2f\\n”,a,b);<br />\r\n}<br />', '学生设计', '无来', '2013-12-10 08:46:07', '4');
INSERT INTO `clang_practice` VALUES ('28', '打鼾风浪', '#include&lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{<br />\r\nfloat a,b,c,t;<br />\r\nscanf(“%f,%f,%f”,&amp;a,&amp;b,&amp;c);<br />\r\nif(a&gt;b)<br />\r\n{<br />\r\nt=a;<br />\r\na=b;<br />\r\nb=t;<br />\r\n}<br />\r\nif(a&gt;c)<br />\r\n{<br />\r\nt=a;<br />\r\na=c;<br />\r\nc=t;<br />\r\n}<br />\r\nif(b&gt;c)<br />\r\n{<br />\r\nt=b;<br />\r\nb=c;<br />\r\nc=t;<br />\r\n}<br />\r\nprintf(“%5.2f,%5.2f,%5.2f\\n”,a,b,c);&nbsp;<br />\r\n}<br />', '学生设计', '输入3个数a、b.c,要求按由小到大的顺', '2013-12-10 08:46:57', '1');
INSERT INTO `clang_practice` VALUES ('29', '求100~200间的全部素数。', '#include&lt;stdio.h&gt;&nbsp;<br />\r\n#include&lt;math.h&gt;<br />\r\nvoid main( )<br />\r\n{<br />\r\n&nbsp;Int m, k, I, n=0;<br />\r\n&nbsp;For(m=1;m&lt;=100;m=m+2)<br />\r\n&nbsp; {<br />\r\nk=sqrt(m);<br />\r\nfor(i=2;i&lt;=k;i++)<br />\r\n&nbsp; if(m%i= =0)<br />\r\n&nbsp; &nbsp; &nbsp;break;<br />\r\nif(i&gt;=k+1)<br />\r\n&nbsp; &nbsp;{&nbsp;<br />\r\n&nbsp; &nbsp; &nbsp;printf(“%d”,m)；<br />\r\n&nbsp; &nbsp; &nbsp; n=n+1;<br />\r\n&nbsp; &nbsp; }<br />\r\nif(n%10= =0)<br />\r\nprintf(“\\n”);<br />\r\n}<br />\r\n&nbsp;printf(“\\n”);&nbsp;<br />\r\n}<br />', '学生设计', '匿名者', '2013-12-10 08:53:11', '1');
INSERT INTO `clang_practice` VALUES ('30', '将一个二维数组a的行和列的元素互换，存在另一个二维数组b中。', '#include&lt;stdio.h&gt;<br />\r\nvoid main( )<br />\r\n{<br />\r\n&nbsp;int a[2][3]={{1,2,3},{4,5,6}}；<br />\r\n&nbsp;int b[3][2],i,j;<br />\r\n&nbsp;printf(“array a:\\n”);<br />\r\n&nbsp;for(i=0;i&lt;=1;i++)<br />\r\n&nbsp;{<br />\r\n&nbsp;for(j=0;j&lt;=2;j++)<br />\r\n&nbsp; {<br />\r\n&nbsp; &nbsp;printf(“%5d”,a[i][j]);<br />\r\n&nbsp; &nbsp;b[j][i]=a[i][j];<br />\r\n&nbsp; &nbsp;}<br />\r\n&nbsp; printf(“\\n”);<br />\r\n&nbsp;}<br />\r\nprintf(“array b:\\n”);<br />\r\nfor(i=0;i&lt;=2;i++)<br />\r\n&nbsp; {<br />\r\nfor(j=0;j&lt;=1;j++)<br />\r\n&nbsp;printf(“%5d”,b[i][j]);<br />\r\nprintf(“\\n”);<br />\r\n&nbsp; &nbsp;}<br />\r\n}<br />', '学生设计', '凌风', '2013-12-10 08:54:20', '1');
INSERT INTO `clang_practice` VALUES ('31', '将数组a中n个整数按相反顺序存放', '#include&lt;stdio.h&gt;<br />\r\nvoid main( )<br />\r\n{<br />\r\n&nbsp;void inv(int x[ ],int n);<br />\r\n&nbsp; int i,a[10]={3,7,9,11,0,6,7,5,4,2};<br />\r\n&nbsp;printf(“The original array:\\n”);<br />\r\n&nbsp;for (i=0;i&lt;10;i++)<br />\r\n&nbsp; &nbsp;printf(“%d”,a[i]);<br />\r\nprintf(“\\n”);<br />\r\ninv(a,10);<br />\r\nprintf(“The array has been inverted:\\n”);<br />\r\nfor(i=0;i&lt;10;i++)<br />\r\n&nbsp; printf(%d”,a[i]);<br />\r\nprintf(“\\n”);<br />\r\n}<br />\r\nvoid inv(int x[ ],int in)<br />\r\n{int temp,i,j,m=(n-1)/2;<br />\r\n&nbsp; for(i=0;i&lt;=m;i++)<br />\r\n&nbsp; &nbsp;{j=n-1-I;<br />\r\n&nbsp; &nbsp; &nbsp;temp=x[i];x[i]=x[j];x[j]=temp;<br />\r\n}<br />\r\nreturn;<br />\r\n}<br />', '学生设计', '魏忠轩', '2013-12-10 08:55:00', '2');
INSERT INTO `clang_practice` VALUES ('32', '设圆的半径r=1.5,求圆的面积，圆的周长。用scanf输入数据，输出计算结果，输出时要求有文字说明，取小数点后两位数字。', '#include&lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{float r,l,s;<br />\r\nfloat pi=3.141926;<br />\r\nprintf(“请输入圆的半径r：”)<br />\r\nscanf(“%f”,&amp;r);<br />\r\nl=2*pi*r:<br />\r\ns=r*r*pi;<br />\r\nprintf(“圆的周长为：l=%6.2f\\n”,l);<br />\r\nprintf(“圆的面积为：s=%6.2f\\n”,s);<br />\r\n}<br />', '教师解惑', null, '2013-12-10 09:09:17', '1');
INSERT INTO `clang_practice` VALUES ('33', '求1！+2!+3！+4！+…+20!', '#include&lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{float s=0,t=1;<br />\r\nint n;<br />\r\nfor (n=1;n&lt;=20;n++)<br />\r\n{<br />\r\nt=t*n;<br />\r\ns=s+t;<br />\r\n}<br />\r\nprintf(”1!+2!+…+20!=%e\\n”,s);<br />\r\n}<br />', '教师解惑', null, '2013-12-10 09:09:46', null);
INSERT INTO `clang_practice` VALUES ('34', '输出所有的水仙花数，所谓的水仙花数是指一个三位数，其各位数字立方和等于该数本身。', '#include&lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{<br />\r\nint I ,j,k,n;<br />\r\nprintf(”narcissus numbers are”);<br />\r\nfor (n=100;n&lt;1000;n++)<br />\r\n{<br />\r\ni=n/100;<br />\r\nj=n/10-i*10;<br />\r\nk=n%10;<br />\r\nif(n==i*i*i+j*j*j+k*k*k)<br />\r\nprintf(”%d”,n);<br />\r\n}<br />\r\nprintf(”\\n”);<br />\r\n}<br />\r\n<div>\r\n	<br />\r\n</div>', '教师解惑', null, '2013-12-10 09:09:58', null);
INSERT INTO `clang_practice` VALUES ('35', '一个数如果恰好等于它的因子之和，这个数就称为完数。例如，6的因子为1,2,3，而6=1+2+3，因此6是完数。编程序找出1000之内的所有完数，并按照下面格式输出其因子;', '#include&lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{int m,s,i;<br />\r\nfor (m=2;m&lt;1000;m++)<br />\r\n{s=0;<br />\r\nfor(i=1;i&lt;m;i++)<br />\r\nif((m%i)==0) s=s+i；<br />\r\nif(s==m)<br />\r\n{printf(”%d,its factors are ”,m);<br />\r\nfor(i=1;i&lt;m;i++)<br />\r\nif(m%i==0) &nbsp; &nbsp;printf(”%d”,i);<br />\r\nprintf(”\\n”);<br />\r\n}<br />\r\n}<br />\r\n}<br />', '教师解惑', null, '2013-12-10 09:10:18', null);
INSERT INTO `clang_practice` VALUES ('36', '有一分数序列：2/1,3/2,5/3,8/5,13/8,21/13…求出这个序列的前20项之和。', '#include&lt;stdio.h&gt;<br />\r\nvoid main()<br />\r\n{int i,t,n=20;<br />\r\nfloat a=2,b=1,s=0;<br />\r\nfor(i=1;i&lt;=n;i++)<br />\r\n{s=s+a/b;<br />\r\nt=a;<br />\r\na=a+b;<br />\r\nb=t;<br />\r\n}<br />\r\nprintf(“sum=%9.6f\\n”,s);<br />\r\n}<br />', '教师解惑', null, '2013-12-10 09:10:35', null);

-- ----------------------------
