-- ----------------------------
-- Table structure for `clang_results`
-- ----------------------------
DROP TABLE IF EXISTS `clang_results`;
CREATE TABLE `clang_results` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `title` varchar(255) DEFAULT NULL COMMENT '标题',
  `pic` varchar(255) DEFAULT NULL COMMENT '奖状图',
  `content` text COMMENT '内容描述',
  `type` varchar(10) DEFAULT NULL COMMENT '学生、教师荣誉',
  `author` varchar(20) DEFAULT NULL COMMENT '发布者',
  `time` datetime DEFAULT NULL COMMENT '发布时间',
  `click` int(11) DEFAULT NULL COMMENT '点击量',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=12 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of clang_results
-- ----------------------------
INSERT INTO `clang_results` VALUES ('1', '我院学子赴广西大学参加第三届全国软件设计', '1386648426.jpg', '<p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;4月7日，经过初赛选拔出来的由我院数学系、物电系和计信系等系别的20名学子在大赛负责人易云飞老师的带领下赴广西大学赛点参加第三届“蓝桥杯”全国软件专业人才设计与创业大赛复赛。本次大赛以“立足行业，结合实际，实战演练，促进就业”为特色，吸引了来自区内各院校的学子们一展软件开发领域的专业技能和创新水平。\r\n	</p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;据悉，此次大赛分为本科组和高职高专组两个组别，共设立“JAVA软件开发”和“C/C++语言程序设计”两个科目，参赛选手将参与4小时的全封闭比赛；选拔赛采用统一命题、分赛区比赛的形式，考察包括填空题和编程题等两种题型。复赛获得一等奖的选手将获得入围全国总决赛资格，参加今年5月份在北京举行的全国总决赛。\r\n	</p>\r\n	<p>\r\n		&nbsp;&nbsp;&nbsp;&nbsp;第三届“蓝桥杯”全国软件专业人才设计与创业大赛是由工业和信息化部人才交流中心、教育部高等学校计算机科学与技术教学指导委员会联合主办，中国软件行业协会、教育部高等学校高职高专计算机类专业教学指导委员会协办的全国性学科竞赛，旨在为行业提供公正、科学的全国性人才选拔与培养平台，加快高等学校计算机与软件课程的教学创新与改革，培养与提升当代大学生的创新与实践能力，为行业培养和选拔一批优秀的软件人才。\r\n	</p>\r\n</p>\r\n<p>\r\n	<br />\r\n</p>', '学生荣誉', 'hong', '2013-12-10 12:07:06', '12');
INSERT INTO `clang_results` VALUES ('2', '我系召开参加第三届全国软件设计大赛总结暨表彰大会', '1386648801.jpg', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;本次大赛由教育部高等学校计算机科学与技术教学指导委员会和工业和信息化部人才中心主办，北京大学软件与微电子学院和北京航空航天大学沙河校区管委会承办，中国软件行业协会、教育部高等学校高职高专计算机类专业教学指导委员会等单位和企业协办，旨在推动软件技术专业课程体系和课程内容改革，提高学生工程实践素质以及学生分析和解决实际问题的能力，为软件行业输送具有创新能力和系统设计能力的高端人才及技术人才，赛事规格高，规模大，是检验和锻炼师生水平的良好平台。学院高度重视此次大赛，为了选拔更加优秀的学生参赛，在2月和4月分别组织学生参加了学院选拔赛和在广西大学举办的赛区比赛。由学院计算机与信息科学系易云飞老师负责的教师指导团队，从学校初赛开始即对学生进行了全程精心辅导，他也被授予“优秀指导教师”荣誉称号。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;此次大赛，国务院参事，大赛组委会主任，原国务院信息化工作办公室常务副主任曲维枝女士，中国工程院院士、大赛专家指导委员会首席专家倪光南先生、教育部高校学生司王辉副司长等60多位领导嘉宾出席了颁奖典礼。IBM、Intel、Manpower、中国航天科技集团、百度、东软、亚信联创、亿阳信通、东华软件、文思创新、博彦科技、先进数通、歌华有线、方正国际、同方鼎欣、大唐软件、中软融鑫、鼎捷软件、联龙博通、融易通、同方威视、加中贸易理事会、天泽投资、立达天使投资、远程教育基金会等知名企业也派代表出席。据悉，总决赛三等奖及以上选手，如果获得本校免试推研资格，将获得北京大学软件与微电子学院、北京交通大学、北京信息科技大学、西南大学、华南理工大学等众多知名院校的面试资格，并优先录取为硕士研究生。各学校均反馈大赛是学校软件专业教育体系的有益补充，一方面起到以赛促学的效果，一方面也是检验教育成果的一杆标尺。竞赛的方式和结果，客观上可为相关专业的教师完善教学方法提供参考和帮助。东软、亚信联创等一大批知名软件企业也表示，通过大赛这个平台可以向院校传达企业对软件专业毕业生的能力需求标准，有利于院校人才培养水平的提高。同时，大赛组委会在决赛期间举办了知名企业现场招聘会，来自百度、中国电信等数十家知名企业在现场向获奖选手提供免笔试直接面试的绿色通道，大赛的获奖选手成为了各大企业争抢的“香饽饽”。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;我院一直都非常注重学生实践动手能力的培养，鼓励学生以赛代练，以赛促学，通过组织参加各种形式的比赛，了解社会对学生能力的需求标准，学习、借鉴相关经验，提升学生的实践能力，创新能力，增强其综合素质，不断提高应用型人才培养水平。\r\n</p>', '教师荣誉', 'hong', '2013-12-10 12:13:21', '16');
INSERT INTO `clang_results` VALUES ('3', '我院学子赴广西大学参加第三届全国软件设计大赛复赛', '1386648372.jpg', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;4月7日，经过初赛选拔出来的由我院数学系、物电系和计信系等系别的20名学子在大赛负责人易云飞老师的带领下赴广西大学赛点参加第三届“蓝桥杯”全国软件专业人才设计与创业大赛复赛。本次大赛以“立足行业，结合实际，实战演练，促进就业”为特色，吸引了来自区内各院校的学子们一展软件开发领域的专业技能和创新水平。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;据悉，此次大赛分为本科组和高职高专组两个组别，共设立“JAVA软件开发”和“C/C++语言程序设计”两个科目，参赛选手将参与4小时的全封闭比赛；选拔赛采用统一命题、分赛区比赛的形式，考察包括填空题和编程题等两种题型。复赛获得一等奖的选手将获得入围全国总决赛资格，参加今年5月份在北京举行的全国总决赛。\r\n</p>', '学生荣誉', 'hong', '2013-12-10 12:06:12', '12');
INSERT INTO `clang_results` VALUES ('4', '全国软件大赛我院学子获佳绩', '1386648271.jpg', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;总决赛三等奖及以上选手，如果获得本校免试推研资格，将获得北京大学软件与微电子学院、北京交通大学、北京信息科技大学、西南大学、华南理工大学等知名院校的面试资格，并优先录取为硕士研究生。东软、亚信联创等一大批知名软件企业也表示，通过大赛这个平台可以向院校传达企业对软件专业毕业生的能力需求标准，有利于院校人才培养水平的提高。同时，这些知名企业也向获奖选手抛去了橄榄枝，大赛的获奖选手成为了各大企业争抢的“香饽饽”。另外，在IBM公司的支持下，大赛组委会将邀请部分获奖选手提交软件创业设计思路，组织专家予以评选和指导，对于优秀的作品提供孵化的机会，进一步培养大学生的创业创新能力。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;计信系领导高度重视此项赛事，为了选拔更加优秀的学生参赛，在4月和5月分别组织学生参加了学院选拔赛和在广西大学举办的赛区比赛。由易云飞老师负责的教师指导团队，从学校初赛开始即对学生进行了全程精心辅导，他也被授予“优秀指导教师”荣誉称号。\r\n</p>\r\n<!--内容关联投票-->', '学生荣誉', 'hong', '2013-12-10 12:06:37', '17');
INSERT INTO `clang_results` VALUES ('5', '全国软件大赛我院学子获佳绩', '1386648193.jpg', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;7月16日，历时近五个月的2011届“国信蓝点杯”全国软件专业人才设计与开发大赛在北京大学百周年纪念讲堂落下帷幕。我院伍悦、王敏两位同学经过连续5个小时的比拼，最终分别获Java本科组全国总决赛二等奖和三等奖。据悉，广西区内本科院校中，Java本科组成绩最好的三所院校分别是桂林电子科技大学、广西大学和河池学院。这是我院学生在国内较高水平软件设计与开发赛事中的一大突破。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;本次大赛由教育部高等学校计算机科学与技术教学指导委员会和工业和信息化部人才中心主办，北京大学软件与微电子学院承办，中国软件行业协会、教育部高等学校高职高专计算机类专业教学指导委员会协办。国务院参事，大赛组委会主任，原国务院信息化工作办公室常务副主任曲维枝女士，中国工程院院士、大赛专家指导委员会首席专家倪光南先生等出席了颁奖典礼。教育部职业教育与成人教育司葛道凯司长、人力资源和社会保障部专业技术人员管理司李金生副司长、工业和信息化部人事教育司衣雪青司长、工业和信息化部办公厅陶少华副主任、工业和信息化部软件服务业司郭建兵副司长、中国软件行业协会陈冲理事长，电子工业出版社敖然社长、北京大学软件与微电子学院白志强书记、教育部高等学校计算机科学与技术教学指导委员会秘书长、北京航空航天大学计算机学院院长马殿富教授，工业和信息化部人才交流中心主任石怀成先生、工业和信息化部人才交流中心顾问刘玉珍女士等近50位领导嘉宾。IBM、Intel、Manpower、东软、亚信联创、方正、东华软件、博彦科技、中科软、同方鼎欣、利达智通等知名企业也派代表出席了颁奖典礼。比赛旨在推动软件技术专业课程体系和课程内容改革，提高学生工程实践素质以及学生分析和解决实际问题的能力，为软件行业输送具有创新能力和系统设计能力的高端人才及技术人才。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;总决赛三等奖及以上选手，如果获得本校免试推研资格，将获得北京大学软件与微电子学院、北京交通大学、北京信息科技大学、西南大学、华南理工大学等知名院校的面试资格，并优先录取为硕士研究生。东软、亚信联创等一大批知名软件企业也表示，通过大赛这个平台可以向院校传达企业对软件专业毕业生的能力需求标准，有利于院校人才培养水平的提高。同时，这些知名企业也向获奖选手抛去了橄榄枝，大赛的获奖选手成为了各大企业争抢的“香饽饽”。另外，在IBM公司的支持下，大赛组委会将邀请部分获奖选手提交软件创业设计思路，组织专家予以评选和指导，对于优秀的作品提供孵化的机会，进一步培养大学生的创业创新能力。\r\n</p>', '学生荣誉', 'hong', '2013-12-10 12:03:13', '6');
INSERT INTO `clang_results` VALUES ('8', '我系召开参加第三届全国软件设计大赛总结暨表彰大会', '1386648545.jpg', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;6月28日下午5时，我系在东区1号教学楼2-507教室召开我院参加第三届“蓝桥杯”全国软件设计大赛总结暨表彰大会。系党总支书记覃运初、系主任黄星寿、副主任罗日才等领导老师和部分学生代表参加会议。会议由系办公室主任吴宝善主持。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;在学院的大力支持下，在我系领导的高度重视下，在指导教师团队的精心指导下，我院学子积极备战，最终在本次大赛中喜获佳绩，为我院赢得了荣誉。其中，林郭隆、蔡永乐两位同学获java本科组全国二等奖，伍悦获java本科组全国三等奖，胡玉波、杨舰获获c/c++本科组全国三等奖，曾海刚等3位同学获广西赛区二等奖，吕国富等4位同学获广西赛区三等奖，李源寿等4位同学获广西赛区优秀奖，黄星寿、易云飞、吴启明、张延旺、唐凤仙等获得优秀指导教师荣誉称号。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;会上，罗日才副主任公布了参赛选手及优秀指导教师获奖名单并鼓励获奖的同学再接再厉，力争再创佳绩。然后由获奖代表蔡永乐同学进行发言，他对领导和老师们给予的关心、鼓励和培养表示由衷的感谢，他感言此次比赛让他们有所收获和成长并表示在今后更加勤奋学习，刻苦钻研，不断提高IT技能和综合素质。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;接着，指导教师代表易云飞发言，他介绍了本次大赛的情况，讲述了获奖同学奋斗经历和在大赛中的收获，激励同学们向获奖同学学习，保持昂扬的精神风貌，积极参加计算机专业各类竞赛，通过各类竞赛，提高专业技能，增强就业能力，总结了参加比赛的经验并谈了下一步的计划和打算。\r\n</p>', '学生荣誉', null, '2013-12-10 12:09:05', null);
INSERT INTO `clang_results` VALUES ('9', '计信系优秀学子分享学习经验——林郭隆', '1386649597.jpg', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;林郭隆,计信系2010级网络工程2班学生，2010年9月进入河池学院学习，2010年任计信系团委、学生会办公室干事，河池学院网络协会软件部干事，滕云动漫社成员，河池学院兵协教练部干事。在2011至2012年当任计信系团委、学生会学习部副部长兼计信系第二课堂负责人，河池学院网络协会软件部副部长。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;在2011年3月份参加了全国计算机二级，并获得全国计算机二级证书。在2011年4月份获得全国软件大赛河池学院选拔赛三等奖，2011年5月获得第二届全国软件大赛（蓝点杯）广西区三等奖，获得2011至2012年专业一等奖学金，2011年10月份获得国家励志奖学金，2011年12月获得第三届河池学院计算机应用技能大赛一等奖，在2011年10月15号参加第六届全国信息技术应用水平大赛预赛并进入复赛，在2011年11月5号于南宁参加复赛并进入决赛，在2011年12月20号获得第六届全国信息技术应用水平大赛java组全国特等奖。于2012年2月获得院优秀共青团员，2012年4月获得第三届全国软件大赛（蓝桥杯）广西区一等奖并进入全国总决赛，2012年5月获得第四届河池学院计算机应用技能大赛二等奖，并在2012年5月第三届全国软件大赛（蓝桥杯）总决赛中获得java组全国二等奖，2012年10月获得校三好学生并获得国家奖学金，在2012年10月13号参加第七届全国信息技术应用水平大赛预赛并进入复赛，在2012年11月3号于南宁参加复赛并进入决赛，获得java组广西区一等奖,在2012年12月18号获得第七届全国信息技术应用水平大赛java组全国一等奖。\r\n</p>\r\n<!--内容关联投票-->', '学生荣誉', null, '2013-12-10 12:26:37', null);
INSERT INTO `clang_results` VALUES ('10', '我院学子在第四届“蓝桥杯”全国软件大赛中再创佳绩', '1386649793.jpg', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;据悉，国务院参事，大赛组委会主任，原国务院信息化工作办公室常务副主任曲维枝女士，教育部高校学生司荆德刚副司长、工业和信息化部软件服务业司李颖巡视员、工业和信息化部人才交流中心王希征主任、电子工业出版社敖然社长等出席颁奖典礼。IBM、亦庄国际投资、东莞松山湖创投、东华软件、博彦科技、先进数通、方正国际、同方鼎欣中软融鑫、金证科技等知名企业也派代表出席了颁奖典礼。\r\n</p>\r\n<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;近年来，我院一直都非常注重培养学生实践动手能力，鼓励学生“以赛代练，以赛促学”，通过组织参加各种形式的比赛，了解社会对学生能力的需求标准，学习、借鉴相关经验，提升学生的实践能力，创新能力，增强其综合素质，不断提高应用型人才培养水平\r\n</p>', '教师荣誉', null, '2013-12-10 12:29:53', '1');
INSERT INTO `clang_results` VALUES ('11', '我院学子在第四届“蓝桥杯”全国软件大赛中再创佳绩', '1386650378.jpg', '<p>\r\n	&nbsp;&nbsp;&nbsp;&nbsp;颁奖典礼上，大赛组委会表彰了取得优异成绩的参赛选手和院校。代表我院参赛的7名同学分别获全国总决赛一等奖2项、二等奖3项，优秀奖2项。其中，我院林晓东同学获JAVA软件开发本科B组一等奖，吕国富同学获JAVA软件开发本科B组二等奖，杨舰、梁国海同学获C/C++程序设计本科B组二等奖，肖伟、林叶川同学获优秀奖；我院黄星寿、罗日才、易云飞和唐凤仙4位老师获“优秀指导教师”。\r\n</p>', '教师荣誉', null, '2013-12-10 12:39:38', '1');

-- ----------------------------
-- Table structure for `clang_team`
-- ----------------------------
DROP TABLE IF EXISTS `clang_team`;
CREATE TABLE `clang_team` (
  `id` int(5) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT '暂无' COMMENT '标题',
  `pic` varchar(255) DEFAULT '暂无' COMMENT '图片地址',
  `content` text COMMENT '内容描述',
  `type` varchar(20) DEFAULT '暂无' COMMENT '类型',
  `author` varchar(20) DEFAULT '暂无' COMMENT '发布者',
  `time` datetime DEFAULT NULL COMMENT '发布时间',
  `click` int(11) DEFAULT NULL COMMENT '点击量',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=29 DEFAULT CHARSET=utf8;

